"""
Module: data_processor
Purpose: Process and validate data entities
"""

import logging
from typing import List, Dict, Optional, Any
from dataclasses import dataclass
from collections import defaultdict

logger = logging.getLogger(__name__)

@dataclass
class DataEntity:
    """Represents a data entity."""
    id: str
    name: str
    value: float
    metadata: Dict[str, Any]

    def validate(self) -> bool:
        """Check if the entity is valid."""
        return bool(self.id and self.name and self.value >= 0)

class DataProcessor:
    """Processes data entity instances."""

    def __init__(self, config: Optional[Dict[str, Any]] = None):
        self.config = config or {}
        self._cache: Dict[str, DataEntity] = {}
        self._stats: Dict[str, int] = defaultdict(int)

    def add(self, item: DataEntity) -> bool:
        """Add an item to the processor."""
        if not item.validate():
            logger.warning(f"Invalid item: {item.id}")
            return False

        self._cache[item.id] = item
        self._stats["added"] += 1
        return True

    def get(self, item_id: str) -> Optional[DataEntity]:
        """Retrieve an item by ID."""
        self._stats["lookups"] += 1
        return self._cache.get(item_id)

    def process_batch(self, items: List[DataEntity]) -> Dict[str, int]:
        """Process a batch of items."""
        results = {"success": 0, "failed": 0}

        for item in items:
            if self.add(item):
                results["success"] += 1
            else:
                results["failed"] += 1

        return results

    def get_stats(self) -> Dict[str, int]:
        """Return processing statistics."""
        return dict(self._stats)

    def clear(self) -> None:
        """Clear all cached items."""
        self._cache.clear()
        self._stats["cleared"] += 1


def main():
    processor = DataProcessor()

    items = [
        DataEntity("1", "first", 10.0, {"type": "A"}),
        DataEntity("2", "second", 20.0, {"type": "B"}),
        DataEntity("3", "", -5.0, {}),  # Invalid
    ]

    results = processor.process_batch(items)
    print(f"Processed: {results}")
    print(f"Stats: {processor.get_stats()}")


if __name__ == "__main__":
    main()

