     1→//! Autopilot - Autonomous task runner with trajectory logging
     2→//!
     3→//! This crate provides a CLI tool that executes tasks via the Claude Agent SDK
     4→//! and logs the complete trajectory in both rlog and JSON formats.
     5→
     6→use std::path::PathBuf;
     7→
     8→pub mod alerts;
     9→pub mod analyze;
    10→pub mod apm;
    11→pub mod apm_parser;
    12→pub mod auto_issues;
    13→pub mod benchmark;
    14→pub mod compaction;
    15→pub mod daemon;
    16→pub mod dashboard;
    17→pub mod goals;
    18→pub mod guardrails;
    19→pub mod learning;
    20→pub mod logs;
    21→pub mod metrics;
    22→pub mod nip_sa_trajectory;
    23→pub mod nostr_agent;
    24→pub mod planmode;
    25→pub mod redact;
    26→pub mod replay;
    27→pub mod rlog;
    28→pub mod state;
    29→pub mod timestamp;
    30→pub mod tool_patterns;
    31→pub mod trajectory;
    32→pub mod trajectory_publisher;
    33→pub mod ui_renderer;
    34→
    35→#[cfg(test)]
    36→mod tests;
    37→
    38→use chrono::Utc;
    39→use claude_agent_sdk::{
    40→    SdkAssistantMessage, SdkMessage, SdkResultMessage, SdkSystemMessage, SdkUserMessage,
    41→};
    42→use serde_json::Value;
    43→use trajectory::{JsonlWriter, StepType, SubagentStatus, TokenUsage, Trajectory, TrajectoryResult};
    44→use rlog::RlogWriter;
    45→
    46→/// Callback invoked when session_id becomes available
    47→pub type SessionIdCallback = Box<dyn FnOnce(&str) + Send>;
    48→
    49→/// Collects SdkMessages into a Trajectory
    50→pub struct TrajectoryCollector {
    51→    trajectory: Trajectory,
    52→    /// Optional rlog writer for streaming output (truncated, human-readable)
    53→    rlog_writer: Option<RlogWriter>,
    54→    /// Path to the rlog file for header updates
    55→    rlog_path: Option<std::path::PathBuf>,
    56→    /// Optional JSONL writer for full data capture (untruncated, Claude Code compatible)
    57→    jsonl_writer: Option<JsonlWriter>,
    58→    /// Callback invoked when session_id is set
    59→    session_id_callback: Option<SessionIdCallback>,
    60→    /// Active subagents: tool_id -> (agent_type, description)
    61→    active_subagents: std::collections::HashMap<String, (String, String)>,
    62→}
    63→
    64→impl TrajectoryCollector {
    65→    /// Create a new collector
    66→    pub fn new(
    67→        prompt: String,
    68→        model: String,
    69→        cwd: String,
    70→        repo_sha: String,
    71→        branch: Option<String>,
    72→    ) -> Self {
    73→        Self {
    74→            trajectory: Trajectory::new(prompt, model, cwd, repo_sha, branch),
    75→            rlog_writer: None,
    76→            rlog_path: None,
    77→            jsonl_writer: None,
    78→            session_id_callback: None,
    79→            active_subagents: std::collections::HashMap::new(),
    80→        }
    81→    }
    82→
    83→    /// Set a callback to be invoked when session_id becomes available
    84→    pub fn on_session_id<F>(&mut self, callback: F)
    85→    where
    86→        F: FnOnce(&str) + Send + 'static,
    87→    {
    88→        self.session_id_callback = Some(Box::new(callback));
    89→    }
    90→
    91→    /// Enable streaming rlog output to a file
    92→    pub fn enable_streaming(&mut self, path: impl AsRef<std::path::Path>) -> std::io::Result<()> {
    93→        let path = path.as_ref();
    94→        let mut writer = RlogWriter::new_streaming(path)?;
    95→        writer.write_header(&self.trajectory)?;
    96→        self.rlog_writer = Some(writer);
    97→        self.rlog_path = Some(path.to_path_buf());
    98→        Ok(())
    99→    }
   100→
   101→    /// Enable JSONL streaming for full data capture (Claude Code compatible format)
   102→    pub fn enable_jsonl_streaming(&mut self, path: impl AsRef<std::path::Path>) -> std::io::Result<()> {
   103→        let mut writer = JsonlWriter::new();
   104→        writer.init(path, &self.trajectory.session_id)?;
   105→        self.jsonl_writer = Some(writer);
   106→        Ok(())
   107→    }
   108→
   109→    /// Set the session_id (useful when resuming a session)
   110→    pub fn set_session_id(&mut self, session_id: String) {
   111→        self.trajectory.session_id = session_id;
   112→    }
   113→
   114→    /// Stream the last added step to rlog file (if streaming is enabled)
   115→    fn stream_last_step(&mut self) {
   116→        if let (Some(writer), Some(last_step)) = (&mut self.rlog_writer, self.trajectory.steps.last()) {
   117→            let _ = writer.append_step(last_step);
   118→        }
   119→    }
   120→
   121→    /// Stream a raw SDK message to JSONL file (if JSONL streaming is enabled)
   122→    fn stream_message_to_jsonl(&mut self, msg: &SdkMessage) {
   123→        if let Some(writer) = &mut self.jsonl_writer {
   124→            // Convert SdkMessage to a serializable format
   125→            let json_value = match msg {
   126→                SdkMessage::System(sys) => {
   127→                    serde_json::json!({
   128→                        "type": "system",
   129→                        "message": sys,
   130→                        "timestamp": chrono::Utc::now().to_rfc3339()
   131→                    })
   132→                }
   133→                SdkMessage::Assistant(asst) => {
   134→                    serde_json::json!({
   135→                        "type": "assistant",
   136→                        "message": asst.message,
   137→                        "timestamp": chrono::Utc::now().to_rfc3339()
   138→                    })
   139→                }
   140→                SdkMessage::User(user) => {
   141→                    serde_json::json!({
   142→                        "type": "user",
   143→                        "message": user.message,
   144→                        "timestamp": chrono::Utc::now().to_rfc3339()
   145→                    })
   146→                }
   147→                SdkMessage::Result(result) => {
   148→                    serde_json::json!({
   149→                        "type": "result",
   150→                        "message": result,
   151→                        "timestamp": chrono::Utc::now().to_rfc3339()
   152→                    })
   153→                }
   154→                SdkMessage::ToolProgress(progress) => {
   155→                    serde_json::json!({
   156→                        "type": "tool_progress",
   157→                        "message": progress,
   158→                        "timestamp": chrono::Utc::now().to_rfc3339()
   159→                    })
   160→                }
   161→                SdkMessage::StreamEvent(event) => {
   162→                    serde_json::json!({
   163→                        "type": "stream_event",
   164→                        "message": event,
   165→                        "timestamp": chrono::Utc::now().to_rfc3339()
   166→                    })
   167→                }
   168→                SdkMessage::AuthStatus(auth) => {
   169→                    serde_json::json!({
   170→                        "type": "auth_status",
   171→                        "message": auth,
   172→                        "timestamp": chrono::Utc::now().to_rfc3339()
   173→                    })
   174→                }
   175→            };
   176→            let _ = writer.write_value(&json_value);
   177→        }
   178→    }
   179→
   180→    /// Process an SdkMessage and add to trajectory
   181→    pub fn process_message(&mut self, msg: &SdkMessage) {
   182→        // Stream raw message to JSONL first (full data capture)
   183→        self.stream_message_to_jsonl(msg);
   184→
   185→        // Then process into trajectory steps (for rlog and analysis)
   186→        match msg {
   187→            SdkMessage::System(sys) => self.process_system(sys),
   188→            SdkMessage::Assistant(asst) => self.process_assistant(asst),
   189→            SdkMessage::User(user) => self.process_user(user),
   190→            SdkMessage::Result(result) => self.process_result(result),
   191→            SdkMessage::ToolProgress(_) => { /* Could track progress */ }
   192→            SdkMessage::StreamEvent(_) => { /* Could track streaming */ }
   193→            SdkMessage::AuthStatus(_) => { /* Could track auth */ }
   194→        }
   195→    }
   196→
   197→    fn process_system(&mut self, sys: &SdkSystemMessage) {
   198→        match sys {
   199→            SdkSystemMessage::Init(init) => {
   200→                self.trajectory.session_id = init.session_id.clone();
   201→                self.trajectory.add_step(StepType::SystemInit {
   202→                    model: init.model.clone(),
   203→                });
   204→
   205→                // Update the header now that we have the session_id
   206→                if let (Some(writer), Some(path)) = (&mut self.rlog_writer, &self.rlog_path) {
   207→                    let _ = writer.update_header(path, &self.trajectory);
   208→                }
   209→
   210→                // Invoke session_id callback if set
   211→                if let Some(callback) = self.session_id_callback.take() {
   212→                    callback(&init.session_id);
   213→                }
   214→
   215→                self.stream_last_step();
   216→            }
   217→            SdkSystemMessage::Status(status) => {
   218→                if let Some(s) = &status.status {
   219→                    self.trajectory.add_step(StepType::SystemStatus {
   220→                        status: format!("{:?}", s),
   221→                    });
   222→                    self.stream_last_step();
   223→                }
   224→            }
   225→            _ => {}
   226→        }
   227→    }
   228→
   229→    fn process_assistant(&mut self, asst: &SdkAssistantMessage) {
   230→        // Extract token info before adding steps (to avoid borrow issues)
   231→        let (tokens_in, tokens_out, tokens_cached) = Self::extract_tokens(&asst.message);
   232→
   233→        // Parse the message content which contains content blocks
   234→        if let Some(content) = asst.message.get("content").and_then(|c| c.as_array()) {
   235→            for block in content {
   236→                let block_type = block.get("type").and_then(|t| t.as_str()).unwrap_or("");
   237→
   238→                match block_type {
   239→                    "thinking" => {
   240→                        let text = block
   241→                            .get("thinking")
   242→                            .and_then(|t| t.as_str())
   243→                            .unwrap_or("");
   244→                        let sig = block
   245→                            .get("signature")
   246→                            .and_then(|s| s.as_str())
   247→                            .map(String::from);
   248→                        let step = self.trajectory.add_step(StepType::Thinking {
   249→                            content: text.to_string(),
   250→                            signature: sig,
   251→                        });
   252→                        step.tokens_in = tokens_in;
   253→                        step.tokens_out = tokens_out;
