   665→/// Extract session_id from a trajectory JSON file.
   666→pub fn extract_session_id_from_json(path: &std::path::Path) -> anyhow::Result<String> {
   667→    let content = std::fs::read_to_string(path)?;
   668→    let traj: trajectory::Trajectory = serde_json::from_str(&content)?;
   669→    if traj.session_id.is_empty() {
   670→        anyhow::bail!("No session_id in trajectory file");
   671→    }
   672→    Ok(traj.session_id)
   673→}
   674→
   675→/// Extract session_id from rlog YAML header.
   676→/// Returns None if the id field is empty or missing.
   677→pub fn extract_session_id_from_rlog(path: &std::path::Path) -> anyhow::Result<Option<String>> {
   678→    let content = std::fs::read_to_string(path)?;
   679→
   680→    // Find YAML header between --- markers
   681→    let lines: Vec<&str> = content.lines().collect();
   682→    if lines.len() < 2 || lines[0] != "---" {
   683→        return Ok(None);
   684→    }
   685→
   686→    // Find end marker
   687→    let end_idx = lines.iter().skip(1).position(|l| *l == "---");
   688→    let end_idx = match end_idx {
   689→        Some(idx) => idx + 1, // +1 because we skipped first line
   690→        None => return Ok(None),
   691→    };
   692→
   693→    // Parse header lines looking for id:
   694→    for line in &lines[1..end_idx] {
   695→        if let Some(rest) = line.strip_prefix("id:") {
   696→            let id = rest.trim();
   697→            if !id.is_empty() {
   698→                return Ok(Some(id.to_string()));
   699→            }
   700→        }
   701→    }
   702→
   703→    Ok(None)
   704→}
   705→
   706→/// Find the workspace root directory by looking for Cargo.toml with `[workspace]`.
   707→///
   708→/// Starts from the current directory and walks up until it finds a Cargo.toml
   709→/// containing `[workspace]`. This ensures autopilot.db is always created at
   710→/// the workspace root, not in subdirectories.
   711→///
   712→/// Returns the current directory if no workspace root is found (fallback).
   713→pub fn find_workspace_root() -> PathBuf {
   714→    let cwd = std::env::current_dir().unwrap_or_else(|_| PathBuf::from("."));
   715→    let mut current = cwd.as_path();
   716→
   717→    loop {
   718→        let cargo_toml = current.join("Cargo.toml");
   719→        if cargo_toml.exists() {
   720→            if let Ok(content) = std::fs::read_to_string(&cargo_toml) {
   721→                if content.contains("[workspace]") {
   722→                    return current.to_path_buf();
   723→                }
   724→            }
   725→        }
   726→
   727→        match current.parent() {
   728→            Some(parent) => current = parent,
   729→            None => break,
   730→        }
   731→    }
   732→
   733→    // Fallback to current directory if no workspace found
   734→    cwd
   735→}
   736→
   737→/// Get the default path for autopilot.db at the workspace root.
   738→///
   739→/// This ensures the database is always created in a consistent location
   740→/// regardless of which subdirectory the command is run from.
   741→pub fn default_db_path() -> PathBuf {
   742→    find_workspace_root().join("autopilot.db")
   743→}
