     1→//! Git diff and status operations
     2→
     3→use anyhow::Result;
     4→use git2::{Diff, DiffOptions, Repository, Status, StatusOptions};
     5→use std::path::Path;
     6→
     7→/// Represents a file change in the working directory
     8→///
     9→/// # Examples
    10→///
    11→/// ```no_run
    12→/// use agentgit::git::{get_status, FileStatus};
    13→/// use std::path::Path;
    14→///
    15→/// # fn example() -> anyhow::Result<()> {
    16→/// let repo_path = Path::new("/path/to/repo");
    17→/// let changes = get_status(repo_path)?;
    18→///
    19→/// for change in changes {
    20→///     match change.status {
    21→///         FileStatus::Modified => println!("Modified: {}", change.path),
    22→///         FileStatus::Untracked => println!("Untracked: {}", change.path),
    23→///         _ => println!("{:?}: {}", change.status, change.path),
    24→///     }
    25→/// }
    26→/// # Ok(())
    27→/// # }
    28→/// ```
    29→#[derive(Debug, Clone)]
    30→pub struct FileChange {
    31→    pub path: String,
    32→    pub status: FileStatus,
    33→}
    34→
    35→/// Status of a file in the working directory
    36→#[derive(Debug, Clone, PartialEq)]
    37→pub enum FileStatus {
    38→    Untracked,
    39→    Modified,
    40→    Added,
    41→    Deleted,
    42→    Renamed,
    43→    Conflicted,
    44→}
    45→
    46→/// Get the status of all files in the working directory
    47→pub fn get_status(repo_path: &Path) -> Result<Vec<FileChange>> {
    48→    let repo = Repository::open(repo_path)?;
    49→
    50→    let mut opts = StatusOptions::new();
    51→    opts.include_untracked(true);
    52→    opts.recurse_untracked_dirs(true);
    53→
    54→    let statuses = repo.statuses(Some(&mut opts))?;
    55→
    56→    let mut changes = Vec::new();
    57→    for entry in statuses.iter() {
    58→        let path = entry.path()
    59→            .unwrap_or("")
    60→            .to_string();
    61→
    62→        let status = match entry.status() {
    63→            s if s.contains(Status::WT_NEW) => FileStatus::Untracked,
    64→            s if s.contains(Status::WT_MODIFIED) => FileStatus::Modified,
    65→            s if s.contains(Status::INDEX_NEW) => FileStatus::Added,
    66→            s if s.contains(Status::WT_DELETED) => FileStatus::Deleted,
    67→            s if s.contains(Status::WT_RENAMED) => FileStatus::Renamed,
    68→            s if s.contains(Status::CONFLICTED) => FileStatus::Conflicted,
    69→            _ => continue,
    70→        };
    71→
    72→        changes.push(FileChange { path, status });
    73→    }
    74→
    75→    Ok(changes)
    76→}
    77→
    78→/// Generate a diff between two commits
    79→pub fn diff_commits(
    80→    repo_path: &Path,
    81→    old_commit_id: &str,
    82→    new_commit_id: &str,
    83→) -> Result<String> {
    84→    let repo = Repository::open(repo_path)?;
    85→
    86→    let old_oid = git2::Oid::from_str(old_commit_id)?;
    87→    let new_oid = git2::Oid::from_str(new_commit_id)?;
    88→
    89→    let old_commit = repo.find_commit(old_oid)?;
    90→    let new_commit = repo.find_commit(new_oid)?;
    91→
    92→    let old_tree = old_commit.tree()?;
    93→    let new_tree = new_commit.tree()?;
    94→
    95→    let diff = repo.diff_tree_to_tree(Some(&old_tree), Some(&new_tree), None)?;
    96→
    97→    format_diff(&diff)
    98→}
    99→
   100→/// Generate a diff of uncommitted changes
   101→pub fn diff_working_directory(repo_path: &Path) -> Result<String> {
   102→    let repo = Repository::open(repo_path)?;
   103→
   104→    let head = repo.head()?;
   105→    let tree = head.peel_to_tree()?;
   106→
   107→    let mut opts = DiffOptions::new();
   108→    let diff = repo.diff_tree_to_workdir_with_index(Some(&tree), Some(&mut opts))?;
   109→
   110→    format_diff(&diff)
   111→}
   112→
   113→/// Generate a patch from a commit range
   114→pub fn generate_patch(
   115→    repo_path: &Path,
   116→    base_commit_id: &str,
   117→    head_commit_id: &str,
   118→) -> Result<String> {
   119→    diff_commits(repo_path, base_commit_id, head_commit_id)
   120→}
   121→
   122→/// Format a git2::Diff as a unified diff string
   123→fn format_diff(diff: &Diff) -> Result<String> {
   124→    let mut patch = String::new();
   125→
   126→    diff.print(git2::DiffFormat::Patch, |_delta, _hunk, line| {
   127→        let content = std::str::from_utf8(line.content()).unwrap_or("");
   128→
   129→        let prefix = match line.origin() {
   130→            '+' => "+",
   131→            '-' => "-",
   132→            ' ' => " ",
   133→            '>' => ">",
   134→            '<' => "<",
   135→            'F' => "File: ",
   136→            'H' => "Hunk: ",
   137→            _ => "",
   138→        };
   139→
   140→        patch.push_str(prefix);
   141→        patch.push_str(content);
   142→
   143→        true
   144→    })?;
   145→
   146→    Ok(patch)
   147→}
   148→
   149→/// Get a summary of changes in a diff
   150→pub fn diff_stats(repo_path: &Path, old_commit_id: &str, new_commit_id: &str) -> Result<DiffStats> {
   151→    let repo = Repository::open(repo_path)?;
   152→
   153→    let old_oid = git2::Oid::from_str(old_commit_id)?;
   154→    let new_oid = git2::Oid::from_str(new_commit_id)?;
   155→
   156→    let old_commit = repo.find_commit(old_oid)?;
   157→    let new_commit = repo.find_commit(new_oid)?;
   158→
   159→    let old_tree = old_commit.tree()?;
   160→    let new_tree = new_commit.tree()?;
   161→
   162→    let diff = repo.diff_tree_to_tree(Some(&old_tree), Some(&new_tree), None)?;
   163→    let stats = diff.stats()?;
   164→
   165→    Ok(DiffStats {
   166→        files_changed: stats.files_changed(),
   167→        insertions: stats.insertions(),
   168→        deletions: stats.deletions(),
   169→    })
   170→}
   171→
   172→/// Statistics about a diff
   173→#[derive(Debug, Clone)]
   174→pub struct DiffStats {
   175→    pub files_changed: usize,
   176→    pub insertions: usize,
   177→    pub deletions: usize,
   178→}
   179→
   180→#[cfg(test)]
   181→mod tests {
   182→    use super::*;
   183→    use git2::Repository;
   184→    use std::fs;
   185→    use tempfile::TempDir;
   186→
   187→    fn create_test_repo() -> (TempDir, Repository) {
   188→        let dir = TempDir::new().unwrap();
   189→        let repo = Repository::init(dir.path()).unwrap();
   190→
   191→        // Configure user
   192→        let mut config = repo.config().unwrap();
   193→        config.set_str("user.name", "Test User").unwrap();
   194→        config.set_str("user.email", "test@example.com").unwrap();
   195→
   196→        (dir, repo)
   197→    }
   198→
   199→    fn create_commit(repo: &Repository, _dir: &Path, message: &str) -> git2::Oid {
   200→        let sig = repo.signature().unwrap();
   201→        let tree_id = {
   202→            let mut index = repo.index().unwrap();
   203→            index.add_all(["."].iter(), git2::IndexAddOption::DEFAULT, None).unwrap();
   204→            index.write().unwrap();
   205→            index.write_tree().unwrap()
   206→        };
   207→        let tree = repo.find_tree(tree_id).unwrap();
   208→
   209→        let parent_commit = if let Ok(head) = repo.head() {
   210→            Some(head.peel_to_commit().unwrap())
   211→        } else {
   212→            None
   213→        };
   214→
   215→        let parents = if let Some(ref p) = parent_commit {
   216→            vec![p]
   217→        } else {
   218→            vec![]
   219→        };
   220→
   221→        repo.commit(
   222→            Some("HEAD"),
   223→            &sig,
   224→            &sig,
   225→            message,
   226→            &tree,
   227→            &parents,
   228→        ).unwrap()
   229→    }
   230→
   231→    #[test]
   232→    fn test_get_status_empty() {
   233→        let (_dir, repo) = create_test_repo();
   234→        let status = get_status(repo.path()).unwrap();
   235→        assert_eq!(status.len(), 0);
   236→    }
   237→
   238→    #[test]
   239→    fn test_get_status_untracked() {
   240→        let (dir, repo) = create_test_repo();
   241→
   242→        // Create a new file
   243→        fs::write(dir.path().join("test.txt"), "hello").unwrap();
   244→
   245→        let status = get_status(repo.path()).unwrap();
   246→        assert_eq!(status.len(), 1);
   247→        assert_eq!(status[0].status, FileStatus::Untracked);
   248→    }
   249→
   250→    #[test]
   251→    fn test_diff_working_directory() {
   252→        let (dir, repo) = create_test_repo();
   253→
   254→        // Create initial file and commit
   255→        fs::write(dir.path().join("test.txt"), "hello\n").unwrap();
   256→        create_commit(&repo, dir.path(), "Initial");
   257→
   258→        // Modify the file
   259→        fs::write(dir.path().join("test.txt"), "hello\nworld\n").unwrap();
   260→
   261→        let diff = diff_working_directory(repo.path()).unwrap();
   262→        assert!(diff.contains("+world"));
   263→    }
   264→
   265→    #[test]
   266→    fn test_generate_patch() {
