     1→//! Diff view rendering with inline comments support
     2→
     3→use maud::{html, Markup};
     4→use nostr::Event;
     5→use std::collections::HashMap;
     6→
     7→/// A line in a diff with optional inline comments
     8→#[derive(Debug, Clone)]
     9→pub struct DiffLine {
    10→    pub line_number: Option<usize>,
    11→    pub content: String,
    12→    pub line_type: DiffLineType,
    13→    pub file_path: String,
    14→}
    15→
    16→/// Type of line in a diff
    17→#[derive(Debug, Clone, Copy, PartialEq, Eq)]
    18→pub enum DiffLineType {
    19→    Addition,
    20→    Deletion,
    21→    Context,
    22→    Header,
    23→}
    24→
    25→/// Comment attached to a specific line in a diff
    26→#[derive(Debug, Clone)]
    27→pub struct InlineComment {
    28→    pub event: Event,
    29→    pub file_path: String,
    30→    pub line_number: usize,
    31→    pub position: LinePosition,
    32→    pub author_pubkey: String,
    33→}
    34→
    35→/// Position of comment relative to line
    36→#[derive(Debug, Clone, Copy, PartialEq, Eq)]
    37→pub enum LinePosition {
    38→    Before,
    39→    After,
    40→}
    41→
    42→/// Parse a unified diff into lines with metadata
    43→pub fn parse_diff_lines(diff_text: &str) -> Vec<DiffLine> {
    44→    let mut lines = Vec::new();
    45→    let mut current_file: Option<String> = None;
    46→    let mut line_number = 0;
    47→
    48→    for line in diff_text.lines() {
    49→        if line.starts_with("diff --git") {
    50→            // Extract file path from: diff --git a/path/to/file b/path/to/file
    51→            if let Some(path) = line.split_whitespace().nth(2) {
    52→                current_file = Some(path.trim_start_matches("a/").to_string());
    53→            }
    54→            lines.push(DiffLine {
    55→                line_number: None,
    56→                content: line.to_string(),
    57→                line_type: DiffLineType::Header,
    58→                file_path: current_file.clone().unwrap_or_default(),
    59→            });
    60→            line_number = 0;
    61→        } else if line.starts_with("@@") {
    62→            // Hunk header
    63→            lines.push(DiffLine {
    64→                line_number: None,
    65→                content: line.to_string(),
    66→                line_type: DiffLineType::Header,
    67→                file_path: current_file.clone().unwrap_or_default(),
    68→            });
    69→        } else if line.starts_with('+') && !line.starts_with("+++") {
    70→            line_number += 1;
    71→            lines.push(DiffLine {
    72→                line_number: Some(line_number),
    73→                content: line.to_string(),
    74→                line_type: DiffLineType::Addition,
    75→                file_path: current_file.clone().unwrap_or_default(),
    76→            });
    77→        } else if line.starts_with('-') && !line.starts_with("---") {
    78→            line_number += 1;
    79→            lines.push(DiffLine {
    80→                line_number: Some(line_number),
    81→                content: line.to_string(),
    82→                line_type: DiffLineType::Deletion,
    83→                file_path: current_file.clone().unwrap_or_default(),
    84→            });
    85→        } else if !line.starts_with("+++") && !line.starts_with("---") {
    86→            // Context line
    87→            line_number += 1;
    88→            lines.push(DiffLine {
    89→                line_number: Some(line_number),
    90→                content: line.to_string(),
    91→                line_type: DiffLineType::Context,
    92→                file_path: current_file.clone().unwrap_or_default(),
    93→            });
    94→        }
    95→    }
    96→
    97→    lines
    98→}
