   100→/// Extract inline comments from NIP-22 comment events with line tags
   101→pub fn extract_inline_comments(comment_events: &[Event]) -> Vec<InlineComment> {
   102→    let mut comments = Vec::new();
   103→
   104→    for event in comment_events {
   105→        // Look for ["line", "file_path", "line_number", "position"] tag
   106→        for tag in &event.tags {
   107→            if tag.len() >= 4 && tag[0] == "line" {
   108→                let file_path = tag[1].clone();
   109→                if let Ok(line_number) = tag[2].parse::<usize>() {
   110→                    let position = match tag[3].as_str() {
   111→                        "before" => LinePosition::Before,
   112→                        "after" => LinePosition::After,
   113→                        _ => LinePosition::After,
   114→                    };
   115→
   116→                    comments.push(InlineComment {
   117→                        event: event.clone(),
   118→                        file_path,
   119→                        line_number,
   120→                        position,
   121→                        author_pubkey: event.pubkey.clone(),
   122→                    });
   123→                }
   124→            }
   125→        }
   126→    }
   127→
   128→    comments
   129→}
   130→
   131→/// Render diff with inline comments
   132→pub fn render_diff_with_comments(
   133→    diff_text: &str,
   134→    comments: &[InlineComment],
   135→    pr_id: &str,
   136→    repo_id: &str,
   137→) -> Markup {
   138→    let lines = parse_diff_lines(diff_text);
   139→
   140→    // Group comments by file and line number
   141→    let mut comment_map: HashMap<(String, usize), Vec<&InlineComment>> = HashMap::new();
   142→    for comment in comments {
   143→        comment_map
   144→            .entry((comment.file_path.clone(), comment.line_number))
   145→            .or_default()
   146→            .push(comment);
   147→    }
   148→
   149→    html! {
   150→        div.diff-container {
   151→            @for (idx, line) in lines.iter().enumerate() {
   152→                @let line_class = match line.line_type {
   153→                    DiffLineType::Addition => "diff-line-add",
   154→                    DiffLineType::Deletion => "diff-line-del",
   155→                    DiffLineType::Context => "diff-line-context",
   156→                    DiffLineType::Header => "diff-line-header",
   157→                };
   158→
   159→                div class={"diff-line " (line_class)} data-line-idx=(idx) {
   160→                    @if let Some(ln) = line.line_number {
   161→                        span.line-number { (ln) }
   162→
   163→                        // Add comment button for reviewable lines
   164→                        @if line.line_type != DiffLineType::Header {
   165→                            button.comment-btn
   166→                                data-file=(line.file_path)
   167→                                data-line=(ln)
   168→                                hx-get={"/repo/" (repo_id) "/pulls/" (pr_id) "/comment-form?file=" (line.file_path) "&line=" (ln)}
   169→                                hx-target={"#comment-form-" (idx)}
   170→                                hx-swap="outerHTML"
   171→                                title="Add comment" {
   172→                                "💬"
   173→                            }
   174→                        }
   175→                    }
   176→
   177→                    pre.line-content { code { (line.content) } }
   178→
   179→                    // Show inline comments for this line
   180→                    @if let Some(line_num) = line.line_number {
   181→                        @if let Some(line_comments) = comment_map.get(&(line.file_path.clone(), line_num)) {
   182→                            div.inline-comments {
   183→                                @for comment in line_comments {
   184→                                    div.inline-comment {
   185→                                        div.comment-header {
   186→                                            span.comment-author { (format_pubkey(&comment.author_pubkey)) }
   187→                                            span.comment-position {
   188→                                                @if comment.position == LinePosition::Before { "↑" } @else { "↓" }
   189→                                            }
   190→                                        }
   191→                                        div.comment-body { (comment.event.content) }
   192→                                    }
   193→                                }
   194→                            }
   195→                        }
   196→                    }
   197→
   198→                    // Placeholder for comment form
   199→                    div id={"comment-form-" (idx)} {}
   200→                }
   201→            }
   202→        }
   203→
   204→        style {
   205→            r#"
   206→.diff-container {
   207→    font-family: 'Monaco', 'Menlo', 'Consolas', monospace;
   208→    font-size: 0.875rem;
   209→    background: #1a1a1a;
   210→    border: 1px solid #333;
   211→}
   212→
   213→.diff-line {
   214→    display: flex;
