   237→}
   238→
   239→.line-number {
   240→    display: inline-block;
   241→    width: 3rem;
   242→    text-align: right;
   243→    color: #666;
   244→    user-select: none;
   245→    padding-right: 0.5rem;
   246→    flex-shrink: 0;
   247→}
   248→
   249→.comment-btn {
   250→    display: none;
   251→    background: none;
   252→    border: none;
   253→    cursor: pointer;
   254→    font-size: 0.875rem;
   255→    padding: 0 0.5rem;
   256→    color: #888;
   257→}
   258→
   259→.diff-line:hover .comment-btn {
   260→    display: inline-block;
   261→}
   262→
   263→.comment-btn:hover {
   264→    color: #4a9eff;
   265→}
   266→
   267→.line-content {
   268→    flex: 1;
   269→    margin: 0;
   270→    white-space: pre-wrap;
   271→    word-break: break-all;
   272→}
   273→
   274→.inline-comments {
   275→    margin-left: 3.5rem;
   276→    padding: 0.5rem;
   277→    border-left: 3px solid #4a9eff;
   278→    background: rgba(74, 158, 255, 0.1);
   279→}
   280→
   281→.inline-comment {
   282→    margin-bottom: 0.5rem;
   283→}
   284→
   285→.inline-comment:last-child {
   286→    margin-bottom: 0;
   287→}
   288→
   289→.comment-header {
   290→    display: flex;
   291→    justify-content: space-between;
   292→    align-items: center;
   293→    margin-bottom: 0.25rem;
   294→    font-size: 0.75rem;
   295→    color: #aaa;
   296→}
   297→
   298→.comment-author {
   299→    font-weight: 600;
   300→}
   301→
