   311→/// Format pubkey for display
   312→fn format_pubkey(pubkey: &str) -> String {
   313→    if pubkey.len() > 16 {
   314→        format!("{}...{}", &pubkey[..8], &pubkey[pubkey.len() - 8..])
   315→    } else {
   316→        pubkey.to_string()
   317→    }
   318→}
   319→
   320→#[cfg(test)]
   321→mod tests {
   322→    use super::*;
   323→
   324→    #[test]
   325→    fn test_parse_diff_lines() {
   326→        let diff = r#"diff --git a/src/main.rs b/src/main.rs
   327→@@ -1,3 +1,5 @@
   328→ fn main() {
   329→+    println!("Hello");
   330→+    println!("World");
   331→ }
   332→-// old comment
   333→"#;
   334→
   335→        let lines = parse_diff_lines(diff);
   336→        assert!(lines.len() > 0);
   337→
   338→        // Check we have additions and deletions
   339→        let has_addition = lines.iter().any(|l| l.line_type == DiffLineType::Addition);
   340→        let has_deletion = lines.iter().any(|l| l.line_type == DiffLineType::Deletion);
   341→        assert!(has_addition);
   342→        assert!(has_deletion);
   343→    }
   344→
   345→    #[test]
   346→    fn test_extract_inline_comments() {
   347→        // This would need a proper Event struct to test
   348→        let comments: Vec<Event> = vec![];
   349→        let inline = extract_inline_comments(&comments);
   350→        assert_eq!(inline.len(), 0);
   351→    }
   352→}
