     7→/// Represents a file change in the working directory
     8→///
     9→/// # Examples
    10→///
    11→/// ```no_run
    12→/// use agentgit::git::{get_status, FileStatus};
    13→/// use std::path::Path;
    14→///
    15→/// # fn example() -> anyhow::Result<()> {
    16→/// let repo_path = Path::new("/path/to/repo");
    17→/// let changes = get_status(repo_path)?;
    18→///
    19→/// for change in changes {
    20→///     match change.status {
    21→///         FileStatus::Modified => println!("Modified: {}", change.path),
    22→///         FileStatus::Untracked => println!("Untracked: {}", change.path),
    23→///         _ => println!("{:?}: {}", change.status, change.path),
    24→///     }
    25→/// }
    26→/// # Ok(())
    27→/// # }
    28→/// ```
    29→#[derive(Debug, Clone)]
    30→pub struct FileChange {
    31→    pub path: String,
    32→    pub status: FileStatus,
    33→}
    34→
    35→/// Status of a file in the working directory
    36→#[derive(Debug, Clone, PartialEq)]
    37→pub enum FileStatus {
    38→    Untracked,
    39→    Modified,
    40→    Added,
    41→    Deleted,
    42→    Renamed,
    43→    Conflicted,
    44→}
    45→
    46→/// Get the status of all files in the working directory
    47→pub fn get_status(repo_path: &Path) -> Result<Vec<FileChange>> {
    48→    let repo = Repository::open(repo_path)?;
    49→
    50→    let mut opts = StatusOptions::new();
    51→    opts.include_untracked(true);
    52→    opts.recurse_untracked_dirs(true);
    53→
    54→    let statuses = repo.statuses(Some(&mut opts))?;
    55→
    56→    let mut changes = Vec::new();
    57→    for entry in statuses.iter() {
    58→        let path = entry.path()
    59→            .unwrap_or("")
    60→            .to_string();
    61→
    62→        let status = match entry.status() {
    63→            s if s.contains(Status::WT_NEW) => FileStatus::Untracked,
    64→            s if s.contains(Status::WT_MODIFIED) => FileStatus::Modified,
    65→            s if s.contains(Status::INDEX_NEW) => FileStatus::Added,
    66→            s if s.contains(Status::WT_DELETED) => FileStatus::Deleted,
    67→            s if s.contains(Status::WT_RENAMED) => FileStatus::Renamed,
    68→            s if s.contains(Status::CONFLICTED) => FileStatus::Conflicted,
    69→            _ => continue,
