    78→/// Generate a diff between two commits
    79→pub fn diff_commits(
    80→    repo_path: &Path,
    81→    old_commit_id: &str,
    82→    new_commit_id: &str,
    83→) -> Result<String> {
    84→    let repo = Repository::open(repo_path)?;
    85→
    86→    let old_oid = git2::Oid::from_str(old_commit_id)?;
    87→    let new_oid = git2::Oid::from_str(new_commit_id)?;
    88→
    89→    let old_commit = repo.find_commit(old_oid)?;
    90→    let new_commit = repo.find_commit(new_oid)?;
    91→
    92→    let old_tree = old_commit.tree()?;
    93→    let new_tree = new_commit.tree()?;
    94→
    95→    let diff = repo.diff_tree_to_tree(Some(&old_tree), Some(&new_tree), None)?;
    96→
    97→    format_diff(&diff)
    98→}
