   100→/// Generate a diff of uncommitted changes
   101→pub fn diff_working_directory(repo_path: &Path) -> Result<String> {
   102→    let repo = Repository::open(repo_path)?;
   103→
   104→    let head = repo.head()?;
   105→    let tree = head.peel_to_tree()?;
   106→
   107→    let mut opts = DiffOptions::new();
   108→    let diff = repo.diff_tree_to_workdir_with_index(Some(&tree), Some(&mut opts))?;
