    pub fn build(self) -> nostr::EventTemplate {
        let mut tags = vec![
            vec!["e".to_string(), self.issue_event_id, String::new(), "root".to_string()],
            vec!["a".to_string(), self.repo_address],
            vec!["p".to_string(), self.assignee_pubkey.clone(), String::new(), "assignee".to_string()],
