     1→//! Provider reputation system for compute marketplace
     2→//!
     3→//! Implements multi-factor reputation tracking with decay, tier system,
     4→//! and tier-based benefits for compute providers.
     5→
     6→use chrono::{DateTime, Utc};
     7→use serde::{Deserialize, Serialize};
     8→use thiserror::Error;
     9→
    10→/// Errors that can occur in the reputation system
    11→#[derive(Debug, Error)]
    12→pub enum ReputationError {
    13→    #[error("Provider not found: {0}")]
    14→    ProviderNotFound(String),
    15→
    16→    #[error("Invalid score: {0}")]
    17→    InvalidScore(String),
    18→
    19→    #[error("Calculation error: {0}")]
    20→    CalculationError(String),
    21→}
    22→
    23→/// Reputation tier classification
    24→#[derive(Debug, Clone, Copy, PartialEq, Eq, PartialOrd, Ord, Serialize, Deserialize)]
    25→#[serde(rename_all = "snake_case")]
    26→pub enum ReputationTier {
    27→    /// New provider: <0.50 overall score, <100 jobs
    28→    New,
    29→    /// Established provider: 0.50-0.79 score, 100+ jobs
    30→    Established,
    31→    /// Trusted provider: 0.80-0.94 score, 500+ jobs, >95% success
    32→    Trusted,
    33→    /// Elite provider: 0.95+ score, 1000+ jobs, >99% success
    34→    Elite,
    35→}
    36→
    37→impl ReputationTier {
    38→    /// Get a description of this tier
    39→    pub fn description(&self) -> &'static str {
    40→        match self {
    41→            Self::New => "New provider establishing track record",
    42→            Self::Established => "Established provider with solid history",
    43→            Self::Trusted => "Trusted provider with excellent performance",
    44→            Self::Elite => "Elite provider with outstanding track record",
    45→        }
    46→    }
    47→
    48→    /// Get the minimum jobs required for this tier
    49→    pub fn min_jobs(&self) -> u64 {
    50→        match self {
    51→            Self::New => 0,
    52→            Self::Established => 100,
    53→            Self::Trusted => 500,
    54→            Self::Elite => 1000,
    55→        }
    56→    }
    57→
    58→    /// Get the minimum score required for this tier
    59→    pub fn min_score(&self) -> f32 {
    60→        match self {
    61→            Self::New => 0.0,
    62→            Self::Established => 0.50,
    63→            Self::Trusted => 0.80,
    64→            Self::Elite => 0.95,
    65→        }
    66→    }
    67→
    68→    /// Get the minimum success rate for this tier
    69→    pub fn min_success_rate(&self) -> f32 {
    70→        match self {
    71→            Self::New => 0.0,
    72→            Self::Established => 0.90,
    73→            Self::Trusted => 0.95,
    74→            Self::Elite => 0.99,
    75→        }
    76→    }
    77→
    78→    /// Calculate tier from metrics
    79→    pub fn from_metrics(overall_score: f32, jobs_completed: u64, success_rate: f32) -> Self {
    80→        if overall_score >= 0.95 && jobs_completed >= 1000 && success_rate >= 0.99 {
    81→            Self::Elite
    82→        } else if overall_score >= 0.80 && jobs_completed >= 500 && success_rate >= 0.95 {
    83→            Self::Trusted
    84→        } else if overall_score >= 0.50 && jobs_completed >= 100 && success_rate >= 0.90 {
    85→            Self::Established
    86→        } else {
    87→            Self::New
    88→        }
    89→    }
    90→}
    91→
    92→/// Track record score component
    93→#[derive(Debug, Clone, Serialize, Deserialize)]
    94→pub struct TrackRecordScore {
    95→    /// Total jobs completed
    96→    pub jobs_completed: u64,
    97→
    98→    /// Success rate (0.0 to 1.0)
    99→    pub success_rate: f32,
   100→
   101→    /// Average latency in milliseconds
   102→    pub avg_latency_ms: u32,
   103→
   104→    /// Uptime percentage (0.0 to 1.0)
   105→    pub uptime_pct: f32,
   106→
   107→    /// Provider age in days
   108→    pub age_days: u32,
   109→}
   110→
   111→impl TrackRecordScore {
   112→    /// Create a new track record score
   113→    pub fn new(
   114→        jobs_completed: u64,
   115→        success_rate: f32,
   116→        avg_latency_ms: u32,
   117→        uptime_pct: f32,
   118→        age_days: u32,
   119→    ) -> Self {
   120→        Self {
   121→            jobs_completed,
   122→            success_rate,
   123→            avg_latency_ms,
   124→            uptime_pct,
   125→            age_days,
   126→        }
   127→    }
   128→
   129→    /// Calculate the track record score (0.0 to 1.0)
   130→    pub fn calculate(&self) -> f32 {
   131→        // Weight components
   132→        let success_weight = 0.40;
   133→        let uptime_weight = 0.30;
   134→        let latency_weight = 0.20;
   135→        let maturity_weight = 0.10;
   136→
   137→        // Success rate contribution
   138→        let success_score = self.success_rate;
   139→
   140→        // Uptime contribution
   141→        let uptime_score = self.uptime_pct;
   142→
   143→        // Latency contribution (normalized, lower is better)
   144→        let latency_score = if self.avg_latency_ms <= 100 {
   145→            1.0
   146→        } else if self.avg_latency_ms >= 5000 {
   147→            0.0
   148→        } else {
   149→            1.0 - ((self.avg_latency_ms - 100) as f32 / 4900.0)
   150→        };
   151→
   152→        // Maturity contribution (capped at 365 days)
   153→        let maturity_score = self.age_days.min(365) as f32 / 365.0;
   154→
   155→        // Weighted average
   156→        (success_score * success_weight
   157→            + uptime_score * uptime_weight
   158→            + latency_score * latency_weight
   159→            + maturity_score * maturity_weight)
   160→            .clamp(0.0, 1.0)
   161→    }
   162→}
   163→
   164→/// Social score component (endorsements, reviews)
   165→#[derive(Debug, Clone, Serialize, Deserialize)]
   166→pub struct SocialScore {
   167→    /// Number of endorsements from other providers
   168→    pub endorsements: u32,
   169→
   170→    /// Average review rating (0.0 to 5.0)
   171→    pub avg_rating: f32,
   172→
   173→    /// Total number of reviews
   174→    pub review_count: u32,
   175→}
   176→
   177→impl SocialScore {
   178→    /// Create a new social score
   179→    pub fn new(endorsements: u32, avg_rating: f32, review_count: u32) -> Self {
   180→        Self {
   181→            endorsements,
   182→            avg_rating,
   183→            review_count,
   184→        }
   185→    }
   186→
   187→    /// Calculate the social score (0.0 to 1.0)
   188→    pub fn calculate(&self) -> f32 {
   189→        // Normalize rating to 0-1
   190→        let rating_score = (self.avg_rating / 5.0).clamp(0.0, 1.0);
   191→
   192→        // Endorsement bonus (diminishing returns)
   193→        let endorsement_score = (self.endorsements as f32 / (self.endorsements as f32 + 10.0)).clamp(0.0, 1.0);
   194→
   195→        // Review count weight (more reviews = more reliable)
   196→        let review_weight = (self.review_count.min(50) as f32 / 50.0).clamp(0.0, 1.0);
   197→
   198→        // Combined score with weights
   199→        (rating_score * 0.6 + endorsement_score * 0.4) * (0.5 + review_weight * 0.5)
   200→    }
   201→}
   202→
   203→impl Default for SocialScore {
   204→    fn default() -> Self {
   205→        Self {
   206→            endorsements: 0,
   207→            avg_rating: 0.0,
   208→            review_count: 0,
   209→        }
   210→    }
   211→}
   212→
   213→/// Economic score component (payment history, disputes)
   214→#[derive(Debug, Clone, Serialize, Deserialize)]
   215→pub struct EconomicScore {
   216→    /// Total earnings in satoshis
   217→    pub total_earnings_sats: u64,
   218→
   219→    /// Number of disputes filed
   220→    pub disputes_filed: u32,
   221→
   222→    /// Number of disputes lost
   223→    pub disputes_lost: u32,
   224→
   225→    /// Payment reliability (0.0 to 1.0)
   226→    pub payment_reliability: f32,
   227→}
   228→
   229→impl EconomicScore {
   230→    /// Create a new economic score
   231→    pub fn new(
   232→        total_earnings_sats: u64,
   233→        disputes_filed: u32,
   234→        disputes_lost: u32,
   235→        payment_reliability: f32,
   236→    ) -> Self {
   237→        Self {
   238→            total_earnings_sats,
   239→            disputes_filed,
   240→            disputes_lost,
   241→            payment_reliability,
   242→        }
   243→    }
   244→
   245→    /// Calculate the economic score (0.0 to 1.0)
   246→    pub fn calculate(&self) -> f32 {
   247→        // Payment reliability is the main factor
   248→        let payment_score = self.payment_reliability;
   249→
   250→        // Dispute penalty
   251→        let dispute_rate = if self.disputes_filed == 0 {
   252→            0.0
   253→        } else {
   254→            self.disputes_lost as f32 / self.disputes_filed as f32
   255→        };
   256→        let dispute_penalty = dispute_rate * 0.3;
   257→
   258→        // Economic activity bonus (diminishing returns)
   259→        let earnings_btc = self.total_earnings_sats as f32 / 100_000_000.0;
   260→        let activity_bonus = (earnings_btc / (earnings_btc + 1.0)).clamp(0.0, 0.2);
   261→
   262→        (payment_score - dispute_penalty + activity_bonus).clamp(0.0, 1.0)
   263→    }
   264→}
