     2→
     3→use anyhow::{anyhow, Result};
     4→use git2::{PushOptions, RemoteCallbacks, Repository};
     5→use std::path::Path;
     6→
     7→/// Push a branch to a remote
     8→pub fn push_branch(
     9→    repo_path: &Path,
    10→    remote_name: &str,
    11→    branch_name: &str,
    12→) -> Result<()> {
    13→    let repo = Repository::open(repo_path)?;
    14→
    15→    // Find the remote
    16→    let mut remote = repo.find_remote(remote_name)?;
    17→
    18→    // Build refspec
    19→    let refspec = format!("refs/heads/{}:refs/heads/{}", branch_name, branch_name);
    20→
    21→    // Set up callbacks for authentication
    22→    let mut callbacks = RemoteCallbacks::new();
    23→    callbacks.credentials(|_url, username_from_url, _allowed_types| {
    24→        git2::Cred::ssh_key_from_agent(username_from_url.unwrap_or("git"))
    25→    });
    26→
    27→    // Push
    28→    let mut push_opts = PushOptions::new();
    29→    push_opts.remote_callbacks(callbacks);
    30→
    31→    remote.push(&[&refspec], Some(&mut push_opts))?;
    32→
    33→    Ok(())
    34→}
    35→
    36→/// Fetch from a remote
    37→pub fn fetch_remote(repo_path: &Path, remote_name: &str) -> Result<()> {
    38→    let repo = Repository::open(repo_path)?;
    39→
    40→    // Find the remote
    41→    let mut remote = repo.find_remote(remote_name)?;
    42→
    43→    // Set up callbacks for authentication
    44→    let mut callbacks = RemoteCallbacks::new();
    45→    callbacks.credentials(|_url, username_from_url, _allowed_types| {
    46→        git2::Cred::ssh_key_from_agent(username_from_url.unwrap_or("git"))
    47→    });
    48→
    49→    // Fetch
    50→    let mut fetch_opts = git2::FetchOptions::new();
    51→    fetch_opts.remote_callbacks(callbacks);
    52→
    53→    remote.fetch(&[] as &[&str], Some(&mut fetch_opts), None)?;
    54→
    55→    Ok(())
    56→}
    57→
    58→/// Add a remote
    59→pub fn add_remote(repo_path: &Path, remote_name: &str, url: &str) -> Result<()> {
    60→    let repo = Repository::open(repo_path)?;
    61→    repo.remote(remote_name, url)?;
    62→    Ok(())
    63→}
    64→
    65→/// List all remotes
    66→pub fn list_remotes(repo_path: &Path) -> Result<Vec<String>> {
    67→    let repo = Repository::open(repo_path)?;
    68→    let remotes = repo.remotes()?;
    69→
    70→    let remote_names: Vec<String> = remotes
    71→        .iter()
    72→        .flatten()
    73→        .map(|name| name.to_string())
    74→        .collect();
    75→
    76→    Ok(remote_names)
    77→}
    78→
    79→/// Get the URL of a remote
    80→pub fn get_remote_url(repo_path: &Path, remote_name: &str) -> Result<String> {
    81→    let repo = Repository::open(repo_path)?;
    82→    let remote = repo.find_remote(remote_name)?;
    83→
    84→    remote.url()
    85→        .ok_or_else(|| anyhow!("Remote URL not found"))
    86→        .map(|s| s.to_string())
    87→}
