     1→//! Mock Nostr Relay for Integration Testing
     2→//!
     3→//! Provides an in-memory Nostr relay implementation for testing Nostr client
     4→//! functionality without requiring a real relay connection.
     5→//!
     6→//! # Example
     7→//!
     8→//! ```no_run
     9→//! use testing::MockRelay;
    10→//!
    11→//! #[tokio::test]
    12→//! async fn test_publish_event() {
    13→//!     let relay = MockRelay::start().await;
    14→//!
    15→//!     // Connect your client to relay.url()
    16→//!     // Publish events and verify they're stored
    17→//!
    18→//!     relay.shutdown().await;
    19→//! }
    20→//! ```
    21→
    22→use anyhow::Result;
    23→use nostr::Event;
    24→use serde_json::json;
    25→use std::collections::HashMap;
    26→use std::sync::Arc;
    27→use tokio::sync::RwLock;
    28→use warp::Filter as WarpFilter;
    29→
    30→/// Mock Nostr relay for testing
    31→///
    32→/// Note: The server runs in the background and cannot be gracefully shut down
    33→/// with warp 0.4. Tests should use unique ports to avoid conflicts.
    34→pub struct MockRelay {
    35→    /// URL of the mock relay (ws://127.0.0.1:PORT)
    36→    url: String,
    37→    /// Shared state between WebSocket handlers
    38→    state: Arc<RelayState>,
    39→}
    40→
    41→#[derive(Default)]
    42→struct RelayState {
    43→    /// All events stored in the relay (id -> event)
    44→    events: RwLock<HashMap<String, Event>>,
    45→    /// Active subscriptions (subscription_id -> filter JSON)
    46→    subscriptions: RwLock<HashMap<String, serde_json::Value>>,
    47→}
    48→
    49→impl MockRelay {
    50→    /// Start a new mock relay on a random available port
    51→    pub async fn start() -> Self {
    52→        Self::start_on_port(0).await
    53→    }
    54→
    55→    /// Start a new mock relay on a specific port (0 = random)
    56→    pub async fn start_on_port(port: u16) -> Self {
    57→        let state = Arc::new(RelayState::default());
    58→        let state_clone = state.clone();
    59→        let state_filter = warp::any().map(move || state_clone.clone());
    60→
    61→        // WebSocket route
    62→        let ws_route = warp::path::end()
    63→            .and(warp::ws())
    64→            .and(state_filter)
    65→            .map(|ws: warp::ws::Ws, state: Arc<RelayState>| {
    66→                ws.on_upgrade(move |socket| handle_connection(socket, state))
    67→            });
    68→
    69→        // Bind to port and spawn server
    70→        let addr: std::net::SocketAddr = ([127, 0, 0, 1], port).into();
    71→        let server_fut = warp::serve(ws_route).bind(addr);
    72→
    73→        // For port 0, we need a unique test port
    74→        let actual_port = if port == 0 {
    75→            // Use a random high port for testing
    76→            use std::sync::atomic::{AtomicU16, Ordering};
    77→            static PORT_COUNTER: AtomicU16 = AtomicU16::new(9000);
    78→            PORT_COUNTER.fetch_add(1, Ordering::SeqCst)
    79→        } else {
    80→            port
    81→        };
    82→        let url = format!("ws://127.0.0.1:{}", actual_port);
    83→
    84→        // Spawn server task (fire and forget - warp handles its own lifetime)
    85→        tokio::spawn(server_fut);
    86→
    87→        // Give server a moment to start
    88→        tokio::time::sleep(std::time::Duration::from_millis(100)).await;
    89→
    90→        Self {
    91→            url,
    92→            state: state.clone(),
    93→        }
    94→    }
    95→
    96→    /// Get the WebSocket URL for this relay
    97→    pub fn url(&self) -> &str {
    98→        &self.url
    99→    }
   100→
   101→    /// Get all events stored in the relay
   102→    pub async fn get_events(&self) -> Vec<Event> {
   103→        self.state.events.read().await.values().cloned().collect()
   104→    }
   105→
   106→    /// Get events by kind
   107→    pub async fn get_events_by_kind(&self, kind: u16) -> Vec<Event> {
   108→        self.state
   109→            .events
   110→            .read()
   111→            .await
   112→            .values()
   113→            .filter(|e| e.kind == kind)
   114→            .cloned()
   115→            .collect()
   116→    }
   117→
   118→    /// Get events by author pubkey
   119→    pub async fn get_events_by_author(&self, pubkey: &str) -> Vec<Event> {
   120→        self.state
   121→            .events
   122→            .read()
   123→            .await
   124→            .values()
   125→            .filter(|e| e.pubkey == pubkey)
   126→            .cloned()
   127→            .collect()
   128→    }
   129→
   130→    /// Get event by ID
   131→    pub async fn get_event(&self, id: &str) -> Option<Event> {
   132→        self.state.events.read().await.get(id).cloned()
   133→    }
   134→
   135→    /// Clear all events
   136→    pub async fn clear_events(&self) {
   137→        self.state.events.write().await.clear();
   138→    }
   139→
   140→    /// Get count of stored events
   141→    pub async fn event_count(&self) -> usize {
   142→        self.state.events.read().await.len()
   143→    }
   144→
   145→    /// Store an event directly (for testing)
   146→    ///
   147→    /// This bypasses WebSocket publishing and stores the event directly.
   148→    /// Useful for test setup.
   149→    pub async fn store_event(&self, event: Event) {
   150→        self.state.events.write().await.insert(event.id.clone(), event);
   151→    }
   152→
   153→    /// Shutdown the relay
   154→    ///
   155→    /// Note: With warp 0.4, graceful shutdown is not easily supported.
   156→    /// The server will continue running in the background. Tests should
   157→    /// use unique ports to avoid conflicts.
   158→    pub async fn shutdown(self) {
   159→        // No-op - warp server continues running
   160→        // This method exists for API compatibility
   161→    }
   162→}
   163→
   164→async fn handle_connection(ws: warp::ws::WebSocket, state: Arc<RelayState>) {
   165→    use futures_util::{SinkExt, StreamExt};
   166→
   167→    let (mut tx, mut rx) = ws.split();
   168→
   169→    while let Some(result) = rx.next().await {
   170→        match result {
   171→            Ok(msg) => {
   172→                if let Ok(text) = msg.to_str() {
   173→                    if let Ok(response) = handle_message(text, &state).await {
   174→                        if let Some(resp_msg) = response {
   175→                            let _ = tx.send(warp::ws::Message::text(resp_msg)).await;
   176→                        }
   177→                    }
   178→                }
   179→            }
   180→            Err(_) => break,
   181→        }
   182→    }
   183→}
   184→
   185→async fn handle_message(text: &str, state: &Arc<RelayState>) -> Result<Option<String>> {
   186→    let msg: serde_json::Value = serde_json::from_str(text)?;
   187→
   188→    let msg_type = msg.get(0).and_then(|v| v.as_str()).unwrap_or("");
   189→
   190→    match msg_type {
   191→        "EVENT" => {
   192→            // Client publishing an event
   193→            if let Some(event_value) = msg.get(1) {
   194→                let event: Event = serde_json::from_value(event_value.clone())?;
   195→
   196→                // Verify event signature
   197→                if let Ok(false) | Err(_) = nostr::verify_event(&event) {
   198→                    return Ok(Some(json!(["OK", event.id, false, "invalid: signature verification failed"]).to_string()));
   199→                }
   200→
   201→                // Store event
   202→                state.events.write().await.insert(event.id.clone(), event.clone());
   203→
   204→                // Send OK response
   205→                return Ok(Some(json!(["OK", event.id, true, ""]).to_string()));
   206→            }
   207→        }
   208→        "REQ" => {
   209→            // Client subscribing
   210→            if let Some(sub_id) = msg.get(1).and_then(|v| v.as_str()) {
   211→                // Store filters as JSON (we'll do basic matching)
   212→                let filters = msg.as_array().map(|arr| &arr[2..]).unwrap_or(&[]);
   213→
   214→                // Store subscription
   215→                state.subscriptions.write().await.insert(
   216→                    sub_id.to_string(),
   217→                    json!(filters),
   218→                );
   219→
   220→                // Send EOSE (in real impl, would send matching events first)
   221→                return Ok(Some(json!(["EOSE", sub_id]).to_string()));
   222→            }
   223→        }
   224→        "CLOSE" => {
   225→            // Client closing subscription
   226→            if let Some(sub_id) = msg.get(1).and_then(|v| v.as_str()) {
   227→                state.subscriptions.write().await.remove(sub_id);
   228→            }
   229→        }
   230→        _ => {}
   231→    }
   232→
   233→    Ok(None)
   234→}
   235→
   236→// Helper function for future filter matching implementation
   237→// For now, subscriptions just trigger EOSE
   238→#[allow(dead_code)]
   239→fn event_matches_kind(event: &Event, kind: u16) -> bool {
   240→    event.kind == kind
   241→}
   242→
   243→#[cfg(test)]
   244→mod tests {
   245→    use super::*;
   246→    use nostr::{finalize_event, generate_secret_key, EventTemplate, KIND_SHORT_TEXT_NOTE};
   247→
   248→    #[tokio::test]
   249→    async fn test_relay_starts_and_stops() {
   250→        let relay = MockRelay::start().await;
   251→        assert!(relay.url().starts_with("ws://127.0.0.1:"));
   252→        relay.shutdown().await;
   253→    }
   254→
   255→    #[tokio::test]
   256→    async fn test_relay_stores_events() {
   257→        let relay = MockRelay::start().await;
   258→
   259→        // Create test event
   260→        let sk = generate_secret_key();
   261→        let template = EventTemplate {
