   263→            tags: vec![],
   264→            content: "test message".to_string(),
   265→            created_at: 1234567890,
   266→        };
   267→        let event = finalize_event(&template, &sk).unwrap();
   268→
   269→        // Store directly
   270→        relay.state.events.write().await.insert(event.id.clone(), event.clone());
   271→
   272→        // Verify
