   274→        assert_eq!(relay.get_event(&event.id).await, Some(event.clone()));
   275→
   276→        relay.shutdown().await;
   277→    }
   278→
   279→    #[tokio::test]
   280→    async fn test_get_events_by_kind() {
   281→        let relay = MockRelay::start().await;
   282→
   283→        let sk = generate_secret_key();
   284→
   285→        // Create events of different kinds
   286→        let template1 = EventTemplate {
   287→            kind: 1,
   288→            tags: vec![],
   289→            content: "note 1".to_string(),
   290→            created_at: 1234567890,
   291→        };
   292→        let template2 = EventTemplate {
   293→            kind: 1,
   294→            tags: vec![],
   295→            content: "note 2".to_string(),
   296→            created_at: 1234567891,
   297→        };
   298→        let template3 = EventTemplate {
   299→            kind: 3,
   300→            tags: vec![],
