     1→//! Git clone operations using libgit2
     2→
     3→use anyhow::{Context, Result};
     4→use git2::{FetchOptions, Progress, RemoteCallbacks, Repository};
     5→use std::path::{Path, PathBuf};
     6→use std::sync::{Arc, Mutex};
     7→use tracing::{debug, info};
     8→
     9→/// Progress callback for clone operations
    10→pub struct CloneProgress {
    11→    pub total_objects: usize,
    12→    pub indexed_objects: usize,
    13→    pub received_objects: usize,
    14→    pub received_bytes: usize,
    15→}
    16→
    17→impl CloneProgress {
    18→    #[allow(dead_code)]
    19→    pub fn new() -> Self {
    20→        Self {
    21→            total_objects: 0,
    22→            indexed_objects: 0,
    23→            received_objects: 0,
    24→            received_bytes: 0,
    25→        }
    26→    }
    27→
    28→    #[allow(dead_code)]
    29→    pub fn percentage(&self) -> f32 {
    30→        if self.total_objects == 0 {
    31→            return 0.0;
    32→        }
    33→        (self.received_objects as f32 / self.total_objects as f32) * 100.0
    34→    }
    35→}
    36→
    37→/// Clone a Git repository to the local workspace
    38→///
    39→/// # Arguments
    40→/// * `url` - The Git URL to clone from
    41→/// * `dest_path` - The destination path to clone to
    42→/// * `progress` - Optional progress tracker
    43→pub fn clone_repository(
    44→    url: &str,
    45→    dest_path: &Path,
    46→    progress: Option<Arc<Mutex<CloneProgress>>>,
    47→) -> Result<Repository> {
    48→    info!("Cloning repository from {} to {:?}", url, dest_path);
    49→
    50→    // Ensure parent directory exists
    51→    if let Some(parent) = dest_path.parent() {
    52→        std::fs::create_dir_all(parent)
    53→            .context("Failed to create workspace directory")?;
    54→    }
    55→
    56→    // Set up callbacks for progress tracking
    57→    let mut callbacks = RemoteCallbacks::new();
    58→
    59→    if let Some(progress_tracker) = progress {
    60→        callbacks.transfer_progress(move |stats: Progress| {
    61→            let mut p = progress_tracker.lock().unwrap();
    62→            p.total_objects = stats.total_objects();
    63→            p.indexed_objects = stats.indexed_objects();
    64→            p.received_objects = stats.received_objects();
    65→            p.received_bytes = stats.received_bytes();
    66→
    67→            debug!(
    68→                "Clone progress: {}/{} objects ({:.1}%)",
    69→                stats.received_objects(),
    70→                stats.total_objects(),
    71→                (stats.received_objects() as f32 / stats.total_objects() as f32) * 100.0
    72→            );
    73→            true
    74→        });
    75→    }
    76→
    77→    // Configure fetch options
    78→    let mut fetch_options = FetchOptions::new();
    79→    fetch_options.remote_callbacks(callbacks);
    80→
    81→    // Build clone options
    82→    let mut builder = git2::build::RepoBuilder::new();
    83→    builder.fetch_options(fetch_options);
    84→
    85→    // Clone the repository
    86→    let repo = builder
    87→        .clone(url, dest_path)
    88→        .context(format!("Failed to clone repository from {}", url))?;
    89→
    90→    info!("Successfully cloned repository to {:?}", dest_path);
    91→    Ok(repo)
    92→}
    93→
    94→/// Get the default workspace path for cloned repositories
    95→pub fn get_workspace_path() -> PathBuf {
    96→    dirs::data_local_dir()
    97→        .unwrap_or_else(|| PathBuf::from("."))
    98→        .join("agentgit")
    99→        .join("repos")
   100→}
   101→
   102→/// Check if a repository is already cloned
   103→pub fn is_repository_cloned(repo_identifier: &str) -> bool {
   104→    let path = get_workspace_path().join(repo_identifier);
   105→    path.exists() && Repository::open(&path).is_ok()
   106→}
   107→
   108→/// Get the local path for a cloned repository
   109→pub fn get_repository_path(repo_identifier: &str) -> PathBuf {
   110→    get_workspace_path().join(repo_identifier)
   111→}
   112→
   113→#[cfg(test)]
   114→mod tests {
   115→    use super::*;
   116→
   117→    #[test]
   118→    fn test_workspace_path() {
   119→        let path = get_workspace_path();
   120→        assert!(path.to_string_lossy().contains("agentgit"));
   121→        assert!(path.to_string_lossy().contains("repos"));
   122→    }
   123→
   124→    #[test]
   125→    fn test_repository_path() {
   126→        let path = get_repository_path("test-repo");
   127→        assert!(path.to_string_lossy().contains("test-repo"));
   128→    }
   129→}
   130→

