     1→//! Compare trajectory tool calls to git diffs
     2→//!
     3→//! Verifies that the diff in a PR matches the tool calls recorded in the trajectory.
     4→
     5→use anyhow::Result;
     6→use serde::{Deserialize, Serialize};
     7→use std::collections::HashSet;
     8→
     9→/// Result of comparing trajectory to diff
    10→#[derive(Debug, Clone, PartialEq, Eq, Serialize, Deserialize)]
    11→pub struct ComparisonResult {
    12→    /// Overall match status
    13→    pub status: MatchStatus,
    14→    /// Files that appear in both trajectory and diff
    15→    pub matched_files: Vec<String>,
    16→    /// Files in trajectory but not in diff
    17→    pub missing_in_diff: Vec<String>,
    18→    /// Files in diff but not in trajectory
    19→    pub extra_in_diff: Vec<String>,
    20→    /// Detailed comparison notes
    21→    pub notes: Vec<String>,
    22→}
    23→
    24→/// Match status between trajectory and diff
    25→#[derive(Debug, Clone, Copy, PartialEq, Eq, Serialize, Deserialize)]
    26→pub enum MatchStatus {
    27→    /// Perfect match
    28→    FullMatch,
    29→    /// Minor discrepancies (whitespace, formatting)
    30→    MinorDiscrepancy,
    31→    /// Major discrepancies (missing files, different changes)
    32→    MajorDiscrepancy,
    33→}
    34→
    35→/// File modification from trajectory tool calls
    36→#[derive(Debug, Clone)]
    37→pub struct TrajectoryModification {
    38→    pub file_path: String,
    39→    pub tool: ToolType,
    40→    pub line_count: Option<usize>,
    41→}
    42→
    43→/// Type of tool used in trajectory
    44→#[derive(Debug, Clone, PartialEq, Eq)]
    45→pub enum ToolType {
    46→    Read,
    47→    Edit,
    48→    Write,
    49→    MultiEdit,
    50→    NotebookEdit,
    51→    Other(String),
    52→}
    53→
    54→/// File change from git diff
    55→#[derive(Debug, Clone)]
    56→pub struct DiffChange {
    57→    pub file_path: String,
    58→    pub additions: usize,
    59→    pub deletions: usize,
    60→}
    61→
    62→/// Compare trajectory tool calls to git diff
    63→///
    64→/// # Examples
    65→///
    66→/// ```no_run
    67→/// use agentgit::trajectory::{compare_trajectory_to_diff, TrajectoryModification, ToolType};
    68→///
    69→/// let trajectory_mods = vec![
    70→///     TrajectoryModification {
    71→///         file_path: "src/main.rs".to_string(),
    72→///         tool: ToolType::Edit,
    73→///         line_count: Some(5),
    74→///     },
    75→/// ];
    76→///
    77→/// let diff_text = r#"
    78→/// diff --git a/src/main.rs b/src/main.rs
    79→/// +++ b/src/main.rs
    80→/// @@ -1,3 +1,8 @@
    81→/// +fn main() {
    82→/// +    println!("Hello");
    83→/// +}
    84→/// "#;
    85→///
    86→/// let result = compare_trajectory_to_diff(&trajectory_mods, diff_text).unwrap();
    87→/// println!("Match status: {:?}", result.status);
    88→/// ```
    89→pub fn compare_trajectory_to_diff(
    90→    trajectory_mods: &[TrajectoryModification],
    91→    diff_text: &str,
    92→) -> Result<ComparisonResult> {
    93→    // Extract files modified in trajectory
    94→    let trajectory_files: HashSet<String> = trajectory_mods
    95→        .iter()
    96→        .filter(|m| matches!(m.tool, ToolType::Edit | ToolType::Write | ToolType::MultiEdit | ToolType::NotebookEdit))
    97→        .map(|m| normalize_path(&m.file_path))
    98→        .collect();
    99→
   100→    // Parse diff to extract changed files
   101→    let diff_changes = parse_diff(diff_text)?;
   102→    let diff_files: HashSet<String> = diff_changes
   103→        .iter()
   104→        .map(|c| normalize_path(&c.file_path))
   105→        .collect();
   106→
   107→    // Compare file sets
   108→    let matched: Vec<String> = trajectory_files
   109→        .intersection(&diff_files)
   110→        .cloned()
   111→        .collect();
   112→
   113→    let missing_in_diff: Vec<String> = trajectory_files
   114→        .difference(&diff_files)
   115→        .cloned()
   116→        .collect();
   117→
   118→    let extra_in_diff: Vec<String> = diff_files
   119→        .difference(&trajectory_files)
   120→        .cloned()
   121→        .collect();
   122→
   123→    // Determine status and generate notes
   124→    let mut notes = Vec::new();
   125→    let status = if missing_in_diff.is_empty() && extra_in_diff.is_empty() {
   126→        notes.push(format!("All {} file(s) match between trajectory and diff", matched.len()));
   127→        MatchStatus::FullMatch
   128→    } else if !missing_in_diff.is_empty() {
   129→        notes.push(format!(
   130→            "{} file(s) in trajectory but not in diff: {}",
   131→            missing_in_diff.len(),
   132→            missing_in_diff.join(", ")
   133→        ));
   134→        MatchStatus::MajorDiscrepancy
   135→    } else {
   136→        notes.push(format!(
   137→            "{} file(s) in diff but not in trajectory (may be auto-generated or manual edits)",
   138→            extra_in_diff.len()
   139→        ));
   140→        MatchStatus::MinorDiscrepancy
   141→    };
   142→
   143→    Ok(ComparisonResult {
   144→        status,
   145→        matched_files: matched,
   146→        missing_in_diff,
   147→        extra_in_diff,
   148→        notes,
   149→    })
   150→}
