   152→/// Parse trajectory events JSON to extract file modifications
   153→///
   154→/// # Examples
   155→///
   156→/// ```
   157→/// use agentgit::trajectory::parse_trajectory_events;
   158→///
   159→/// let events_json = vec![
   160→///     r#"{"type":"ToolUse","tool":"Edit","input":{"file_path":"src/main.rs"}}"#.to_string(),
   161→///     r#"{"type":"ToolResult","success":true}"#.to_string(),
   162→/// ];
   163→///
   164→/// let mods = parse_trajectory_events(&events_json).unwrap();
   165→/// assert_eq!(mods.len(), 1);
   166→/// assert_eq!(mods[0].file_path, "src/main.rs");
   167→/// ```
   168→pub fn parse_trajectory_events(events_json: &[String]) -> Result<Vec<TrajectoryModification>> {
   169→    let mut modifications = Vec::new();
   170→
   171→    for event_str in events_json {
   172→        let event: serde_json::Value = serde_json::from_str(event_str)?;
   173→
   174→        // Check if this is a ToolUse event
   175→        if event.get("type").and_then(|t| t.as_str()) != Some("ToolUse") {
   176→            continue;
   177→        }
   178→
   179→        let tool_name = event
   180→            .get("tool")
   181→            .and_then(|t| t.as_str())
   182→            .unwrap_or("unknown");
   183→
   184→        let tool = match tool_name {
   185→            "Read" => ToolType::Read,
   186→            "Edit" => ToolType::Edit,
   187→            "Write" => ToolType::Write,
   188→            "MultiEdit" => ToolType::MultiEdit,
   189→            "NotebookEdit" => ToolType::NotebookEdit,
   190→            other => ToolType::Other(other.to_string()),
   191→        };
   192→
   193→        // Extract file path from input
   194→        if let Some(input) = event.get("input") {
   195→            if let Some(file_path) = input.get("file_path").and_then(|f| f.as_str()) {
   196→                modifications.push(TrajectoryModification {
   197→                    file_path: file_path.to_string(),
   198→                    tool,
   199→                    line_count: None, // Could extract from Edit tool's old_string/new_string
   200→                });
   201→            }
   202→        }
   203→    }
   204→
   205→    Ok(modifications)
   206→}
   207→
   208→/// Parse a unified diff to extract file changes
   209→fn parse_diff(diff_text: &str) -> Result<Vec<DiffChange>> {
   210→    let mut changes = Vec::new();
   211→    let mut current_file: Option<String> = None;
   212→    let mut additions = 0;
   213→    let mut deletions = 0;
   214→
   215→    for line in diff_text.lines() {
   216→        if line.starts_with("diff --git") {
   217→            // Save previous file if exists
   218→            if let Some(file) = current_file.take() {
   219→                changes.push(DiffChange {
   220→                    file_path: file,
   221→                    additions,
   222→                    deletions,
   223→                });
   224→                additions = 0;
   225→                deletions = 0;
   226→            }
   227→
   228→            // Parse new file path
   229→            // Format: diff --git a/path/to/file b/path/to/file
   230→            if let Some(path) = line.split_whitespace().nth(2) {
   231→                current_file = Some(path.trim_start_matches("a/").to_string());
   232→            }
   233→        } else if line.starts_with('+') && !line.starts_with("+++") {
   234→            additions += 1;
   235→        } else if line.starts_with('-') && !line.starts_with("---") {
   236→            deletions += 1;
   237→        }
   238→    }
   239→
   240→    // Don't forget the last file
   241→    if let Some(file) = current_file {
   242→        changes.push(DiffChange {
   243→            file_path: file,
   244→            additions,
   245→            deletions,
   246→        });
   247→    }
   248→
   249→    Ok(changes)
   250→}
   251→
   252→/// Normalize file path for comparison
   253→fn normalize_path(path: &str) -> String {
   254→    // Remove leading slashes, ./, etc.
   255→    path.trim_start_matches('/')
   256→        .trim_start_matches("./")
   257→        .to_string()
   258→}
