     2→//!
     3→//! Provides async connection to a Nostr relay with automatic reconnection,
     4→//! message handling, health monitoring, and automatic event queueing for
     5→//! offline support.
     6→
     7→use crate::error::{ClientError, Result};
     8→use crate::queue::MessageQueue;
     9→use crate::subscription::Subscription;
    10→use futures::{SinkExt, StreamExt};
    11→use nostr::Event;
    12→use serde_json::{json, Value};
    13→use std::collections::HashMap;
    14→use std::sync::Arc;
    15→use std::time::Duration;
    16→use tokio::net::TcpStream;
    17→use tokio::sync::{mpsc, oneshot, Mutex, RwLock};
    18→use tokio::time::timeout;
    19→use tokio_tungstenite::{connect_async, tungstenite::Message, MaybeTlsStream, WebSocketStream};
    20→use tracing::{debug, info, warn};
    21→use url::Url;
    22→
    23→/// Connection state
    24→#[derive(Debug, Clone, Copy, PartialEq, Eq)]
    25→pub enum ConnectionState {
    26→    /// Disconnected
    27→    Disconnected,
    28→    /// Currently connecting
    29→    Connecting,
    30→    /// Connected and ready
    31→    Connected,
    32→    /// Reconnecting after disconnect
    33→    Reconnecting,
    34→}
    35→
    36→/// Relay message received from the relay
    37→#[derive(Debug, Clone)]
    38→pub enum RelayMessage {
    39→    /// EVENT message: ["EVENT", subscription_id, event]
    40→    Event(String, Event),
    41→    /// OK message: ["OK", event_id, success, message]
    42→    Ok(String, bool, String),
    43→    /// EOSE message: ["EOSE", subscription_id]
    44→    Eose(String),
    45→    /// NOTICE message: ["NOTICE", message]
    46→    Notice(String),
    47→    /// AUTH message: ["AUTH", challenge]
    48→    Auth(String),
    49→}
    50→
    51→/// Confirmation result for event publishing
    52→#[derive(Debug, Clone)]
    53→pub struct PublishConfirmation {
    54→    /// Event ID that was published
    55→    pub event_id: String,
    56→    /// Whether the relay accepted the event
    57→    pub accepted: bool,
    58→    /// Message from the relay (empty if accepted, error message if rejected)
    59→    pub message: String,
    60→}
    61→
    62→type ConfirmationSender = oneshot::Sender<PublishConfirmation>;
    63→
    64→/// Relay connection configuration
    65→#[derive(Debug, Clone)]
    66→pub struct RelayConfig {
    67→    /// Connection timeout
    68→    pub connect_timeout: Duration,
    69→    /// Reconnection attempts (0 = infinite)
    70→    pub max_reconnect_attempts: u32,
    71→    /// Initial reconnection delay
    72→    pub reconnect_delay: Duration,
    73→    /// Maximum reconnection delay
    74→    pub max_reconnect_delay: Duration,
    75→    /// Ping interval for health checks
    76→    pub ping_interval: Duration,
    77→    /// Enable message queue for offline support
    78→    pub enable_queue: bool,
    79→    /// Queue retry poll interval
    80→    pub queue_poll_interval: Duration,
    81→}
    82→
    83→impl Default for RelayConfig {
    84→    fn default() -> Self {
    85→        Self {
    86→            connect_timeout: Duration::from_secs(10),
    87→            max_reconnect_attempts: 0, // Infinite
    88→            reconnect_delay: Duration::from_secs(1),
    89→            max_reconnect_delay: Duration::from_secs(60),
    90→            ping_interval: Duration::from_secs(30),
    91→            enable_queue: true,
    92→            queue_poll_interval: Duration::from_secs(5),
    93→        }
    94→    }
    95→}
