   100→pub struct RelayConnection {
   101→    /// Relay URL
   102→    url: Url,
   103→    /// Configuration
   104→    config: RelayConfig,
   105→    /// Connection state
   106→    state: Arc<RwLock<ConnectionState>>,
   107→    /// WebSocket stream
   108→    ws: Arc<Mutex<Option<WsStream>>>,
   109→    /// Incoming message channel (for future background task)
   110→    _rx: Arc<Mutex<mpsc::UnboundedReceiver<RelayMessage>>>,
   111→    /// Outgoing message channel (for future background task)
   112→    _tx: mpsc::UnboundedSender<Value>,
   113→    /// Pending event confirmations (event_id -> oneshot sender)
   114→    pending_confirmations: Arc<Mutex<HashMap<String, ConfirmationSender>>>,
   115→    /// Active subscriptions (subscription_id -> Subscription)
   116→    subscriptions: Arc<Mutex<HashMap<String, Subscription>>>,
   117→    /// Message queue for offline support
   118→    queue: Option<Arc<MessageQueue>>,
   119→    /// Queue processing task handle
   120→    queue_task: Arc<Mutex<Option<tokio::task::JoinHandle<()>>>>,
   121→}
   122→
   123→impl RelayConnection {
   124→    /// Create a new relay connection (does not connect yet)
   125→    pub fn new(url: &str) -> Result<Self> {
   126→        Self::with_config(url, RelayConfig::default())
   127→    }
   128→
   129→    /// Create a new relay connection with custom config
   130→    pub fn with_config(url: &str, config: RelayConfig) -> Result<Self> {
   131→        let url = Url::parse(url)?;
   132→
   133→        // Validate WebSocket URL
   134→        if url.scheme() != "ws" && url.scheme() != "wss" {
   135→            return Err(ClientError::InvalidUrl(format!(
   136→                "URL must use ws:// or wss:// scheme, got: {}",
   137→                url.scheme()
   138→            )));
   139→        }
   140→
   141→        let (_msg_tx, msg_rx) = mpsc::unbounded_channel();
   142→        let (out_tx, _) = mpsc::unbounded_channel();
   143→
   144→        // Create message queue if enabled
   145→        let queue = if config.enable_queue {
   146→            Some(Arc::new(MessageQueue::new().map_err(|e| {
   147→                ClientError::Internal(format!("Failed to create message queue: {}", e))
   148→            })?))
   149→        } else {
   150→            None
   151→        };
   152→
   153→        Ok(Self {
   154→            url,
   155→            config,
   156→            state: Arc::new(RwLock::new(ConnectionState::Disconnected)),
   157→            ws: Arc::new(Mutex::new(None)),
   158→            _rx: Arc::new(Mutex::new(msg_rx)),
   159→            _tx: out_tx,
   160→            pending_confirmations: Arc::new(Mutex::new(HashMap::new())),
   161→            subscriptions: Arc::new(Mutex::new(HashMap::new())),
   162→            queue,
   163→            queue_task: Arc::new(Mutex::new(None)),
   164→        })
   165→    }
   166→
   167→    /// Connect to the relay
   168→    pub async fn connect(&self) -> Result<()> {
   169→        let mut state = self.state.write().await;
   170→        if *state != ConnectionState::Disconnected {
   171→            return Err(ClientError::AlreadyConnected);
   172→        }
   173→        *state = ConnectionState::Connecting;
   174→        drop(state);
   175→
   176→        info!("Connecting to relay: {}", self.url);
   177→
   178→        let ws_stream = match timeout(
   179→            self.config.connect_timeout,
   180→            connect_async(self.url.as_str()),
   181→        )
   182→        .await
   183→        {
   184→            Ok(Ok((stream, _))) => stream,
   185→            Ok(Err(e)) => {
   186→                *self.state.write().await = ConnectionState::Disconnected;
   187→                return Err(ClientError::WebSocket(e.to_string()));
   188→            }
   189→            Err(_) => {
   190→                *self.state.write().await = ConnectionState::Disconnected;
   191→                return Err(ClientError::Timeout(format!(
   192→                    "Connection timeout after {:?}",
   193→                    self.config.connect_timeout
   194→                )));
   195→            }
   196→        };
   197→
   198→        *self.ws.lock().await = Some(ws_stream);
   199→        *self.state.write().await = ConnectionState::Connected;
   200→
   201→        info!("Connected to relay: {}", self.url);
   202→
   203→        // Process any queued messages
   204→        if let Err(e) = self.process_queue().await {
   205→            warn!("Error processing queue after connect: {}", e);
   206→        }
   207→
   208→        // Start queue processing task
   209→        self.start_queue_task();
   210→
   211→        Ok(())
   212→    }
   213→
   214→    /// Disconnect from the relay
   215→    pub async fn disconnect(&self) -> Result<()> {
   216→        let mut state = self.state.write().await;
   217→        if *state == ConnectionState::Disconnected {
   218→            return Ok(());
   219→        }
   220→
   221→        info!("Disconnecting from relay: {}", self.url);
   222→
   223→        // Stop queue processing task
   224→        self.stop_queue_task().await;
   225→
   226→        let mut ws = self.ws.lock().await;
   227→        if let Some(mut stream) = ws.take() {
   228→            let _ = stream.close(None).await;
   229→        }
   230→
   231→        *state = ConnectionState::Disconnected;
   232→        info!("Disconnected from relay: {}", self.url);
   233→        Ok(())
   234→    }
   235→
   236→    /// Get current connection state
   237→    pub async fn state(&self) -> ConnectionState {
   238→        *self.state.read().await
   239→    }
   240→
   241→    /// Check if connected
   242→    pub async fn is_connected(&self) -> bool {
   243→        *self.state.read().await == ConnectionState::Connected
   244→    }
