   252→    /// Publish an event and wait for confirmation from the relay
   253→    ///
   254→    /// This method sends the event and waits for an OK message response.
   255→    /// Returns PublishConfirmation indicating whether the relay accepted the event.
   256→    pub async fn publish_event(
   257→        &self,
   258→        event: &Event,
   259→        confirmation_timeout: Duration,
   260→    ) -> Result<PublishConfirmation> {
   261→        // Create oneshot channel for confirmation
   262→        let (tx, rx) = oneshot::channel();
   263→
   264→        // Get event ID (already hex-encoded string)
   265→        let event_id = event.id.clone();
   266→
   267→        // Register pending confirmation
   268→        {
   269→            let mut confirmations = self.pending_confirmations.lock().await;
   270→            confirmations.insert(event_id.clone(), tx);
   271→        }
   272→
   273→        // Send the event
   274→        if let Err(e) = self.send_event(event).await {
   275→            // Remove pending confirmation on send error
   276→            let mut confirmations = self.pending_confirmations.lock().await;
   277→            confirmations.remove(&event_id);
   278→
   279→            // If not connected and queue is enabled, queue the event for retry
   280→            if matches!(e, ClientError::NotConnected)
   281→                && let Some(ref queue) = self.queue
   282→            {
   283→                queue.enqueue(event, self.url.as_str()).map_err(|queue_err| {
   284→                    warn!("Failed to queue event for retry: {}", queue_err);
   285→                    queue_err
   286→                })?;
   287→                info!("Event queued for retry: {}", event_id);
   288→                // Return success since event is queued
   289→                return Ok(PublishConfirmation {
   290→                    event_id,
   291→                    accepted: true,
   292→                    message: "Queued for retry".to_string(),
   293→                });
   294→            }
   295→
   296→            return Err(e);
   297→        }
   298→
   299→        // Wait for confirmation with timeout
   300→        match timeout(confirmation_timeout, rx).await {
   301→            Ok(Ok(confirmation)) => Ok(confirmation),
   302→            Ok(Err(_)) => {
   303→                // Channel was dropped (shouldn't happen)
   304→                Err(ClientError::PublishFailed(
   305→                    "Confirmation channel closed".to_string(),
   306→                ))
   307→            }
   308→            Err(_) => {
   309→                // Timeout - remove pending confirmation
   310→                let mut confirmations = self.pending_confirmations.lock().await;
   311→                confirmations.remove(&event_id);
   312→                Err(ClientError::Timeout(format!(
   313→                    "Event confirmation timeout after {:?}",
   314→                    confirmation_timeout
   315→                )))
   316→            }
   317→        }
   318→    }
