     6→use std::sync::Arc;
     7→use std::time::{Duration, Instant};
     8→use tokio::sync::RwLock;
     9→use rand::Rng;
    10→
    11→/// Circuit breaker state
    12→#[derive(Debug, Clone, Copy, PartialEq, Eq)]
    13→pub enum CircuitState {
    14→    /// Circuit is closed, requests flow normally
    15→    Closed,
    16→    /// Circuit is open, requests are rejected immediately
    17→    Open,
    18→    /// Circuit is half-open, allowing test requests
    19→    HalfOpen,
    20→}
    21→
    22→/// Circuit breaker for fault tolerance
    23→///
    24→/// Prevents cascading failures by stopping requests to failing services.
    25→#[derive(Debug)]
    26→pub struct CircuitBreaker {
    27→    /// Current state
    28→    state: Arc<RwLock<CircuitState>>,
    29→    /// Failure count in current window
    30→    failures: Arc<RwLock<u32>>,
    31→    /// Success count in current window
    32→    successes: Arc<RwLock<u32>>,
    33→    /// Failure threshold before opening circuit
    34→    failure_threshold: u32,
    35→    /// Success threshold before closing circuit (when half-open)
    36→    success_threshold: u32,
    37→    /// Time to wait before attempting half-open
    38→    timeout: Duration,
    39→    /// Last state change timestamp
    40→    last_change: Arc<RwLock<Instant>>,
    41→}
    42→
    43→impl CircuitBreaker {
    44→    /// Create a new circuit breaker
    45→    ///
    46→    /// # Arguments
    47→    /// * `failure_threshold` - Number of failures before opening (default: 5)
    48→    /// * `success_threshold` - Number of successes to close when half-open (default: 2)
    49→    /// * `timeout` - Time to wait before half-open attempt (default: 30s)
    50→    pub fn new(failure_threshold: u32, success_threshold: u32, timeout: Duration) -> Self {
    51→        Self {
    52→            state: Arc::new(RwLock::new(CircuitState::Closed)),
    53→            failures: Arc::new(RwLock::new(0)),
    54→            successes: Arc::new(RwLock::new(0)),
    55→            failure_threshold,
    56→            success_threshold,
    57→            timeout,
    58→            last_change: Arc::new(RwLock::new(Instant::now())),
    59→        }
    60→    }
    61→
    62→    /// Create with default thresholds
    63→    pub fn default() -> Self {
    64→        Self::new(5, 2, Duration::from_secs(30))
    65→    }
    66→
    67→    /// Get current circuit state
    68→    pub async fn state(&self) -> CircuitState {
    69→        let state = *self.state.read().await;
    70→
    71→        // Check if open circuit should transition to half-open
    72→        if state == CircuitState::Open {
    73→            let elapsed = self.last_change.read().await.elapsed();
    74→            if elapsed >= self.timeout {
    75→                return CircuitState::HalfOpen;
    76→            }
    77→        }
    78→
    79→        state
    80→    }
    81→
    82→    /// Check if a request is allowed
    83→    pub async fn is_allowed(&self) -> bool {
    84→        let state = self.state().await;
    85→
    86→        // Update state if timeout expired
    87→        if state == CircuitState::HalfOpen {
    88→            let mut current_state = self.state.write().await;
    89→            if *current_state == CircuitState::Open {
    90→                *current_state = CircuitState::HalfOpen;
    91→                *self.last_change.write().await = Instant::now();
    92→            }
    93→        }
    94→
    95→        matches!(state, CircuitState::Closed | CircuitState::HalfOpen)
    96→    }
    97→
    98→    /// Record a successful request
    99→    pub async fn record_success(&self) {
   100→        let mut state = self.state.write().await;
   101→
   102→        match *state {
   103→            CircuitState::Closed => {
   104→                // Reset failure count on success
   105→                *self.failures.write().await = 0;
   106→            }
   107→            CircuitState::HalfOpen => {
   108→                let mut successes = self.successes.write().await;
   109→                *successes += 1;
   110→
   111→                // Transition to closed if threshold met
   112→                if *successes >= self.success_threshold {
   113→                    *state = CircuitState::Closed;
   114→                    *successes = 0;
   115→                    *self.failures.write().await = 0;
   116→                    *self.last_change.write().await = Instant::now();
   117→                }
   118→            }
   119→            CircuitState::Open => {
   120→                // Should not happen, but reset if it does
   121→                *state = CircuitState::Closed;
   122→                *self.failures.write().await = 0;
   123→                *self.successes.write().await = 0;
   124→            }
   125→        }
   126→    }
   127→
   128→    /// Record a failed request
   129→    pub async fn record_failure(&self) {
   130→        let mut state = self.state.write().await;
   131→
   132→        match *state {
   133→            CircuitState::Closed => {
   134→                let mut failures = self.failures.write().await;
   135→                *failures += 1;
   136→
   137→                // Open circuit if threshold met
   138→                if *failures >= self.failure_threshold {
   139→                    *state = CircuitState::Open;
   140→                    *self.last_change.write().await = Instant::now();
   141→                }
   142→            }
   143→            CircuitState::HalfOpen => {
   144→                // Failure in half-open means back to open
   145→                *state = CircuitState::Open;
   146→                *self.successes.write().await = 0;
   147→                *self.last_change.write().await = Instant::now();
   148→            }
   149→            CircuitState::Open => {
   150→                // Already open, no action needed
   151→            }
   152→        }
   153→    }
   154→
   155→    /// Reset the circuit breaker to closed state
   156→    pub async fn reset(&self) {
   157→        *self.state.write().await = CircuitState::Closed;
   158→        *self.failures.write().await = 0;
   159→        *self.successes.write().await = 0;
   160→        *self.last_change.write().await = Instant::now();
   161→    }
   162→
   163→    /// Get failure count
   164→    pub async fn failure_count(&self) -> u32 {
   165→        *self.failures.read().await
   166→    }
   167→}
