   169→/// Exponential backoff calculator with jitter
   170→pub struct ExponentialBackoff {
   171→    /// Base delay for first retry
   172→    base_delay: Duration,
   173→    /// Maximum delay cap
   174→    max_delay: Duration,
   175→    /// Current attempt number (0-indexed)
   176→    attempt: u32,
   177→    /// Maximum attempts (0 = infinite)
   178→    max_attempts: u32,
   179→}
   180→
   181→impl ExponentialBackoff {
   182→    /// Create a new exponential backoff calculator
   183→    pub fn new(base_delay: Duration, max_delay: Duration, max_attempts: u32) -> Self {
   184→        Self {
   185→            base_delay,
   186→            max_delay,
   187→            attempt: 0,
   188→            max_attempts,
   189→        }
   190→    }
   191→
   192→    /// Create with default values (1s base, 60s max, infinite attempts)
   193→    pub fn default() -> Self {
   194→        Self::new(
   195→            Duration::from_secs(1),
   196→            Duration::from_secs(60),
   197→            0, // Infinite
   198→        )
   199→    }
   200→
   201→    /// Get next delay with exponential backoff and jitter
   202→    ///
   203→    /// Uses full jitter: delay = random(0, min(max_delay, base * 2^attempt))
   204→    pub fn next_delay(&mut self) -> Option<Duration> {
   205→        // Check if max attempts exceeded
   206→        if self.max_attempts > 0 && self.attempt >= self.max_attempts {
   207→            return None;
   208→        }
   209→
   210→        // Calculate exponential delay: base * 2^attempt
   211→        let exp_delay = self.base_delay.as_millis() as u64 * 2_u64.pow(self.attempt);
   212→        let capped_delay = exp_delay.min(self.max_delay.as_millis() as u64);
   213→
   214→        // Add full jitter: random(0, delay)
   215→        let mut rng = rand::thread_rng();
   216→        let jittered = rng.gen_range(0..=capped_delay);
   217→
   218→        self.attempt += 1;
   219→        Some(Duration::from_millis(jittered))
   220→    }
   221→
   222→    /// Reset backoff to initial state
   223→    pub fn reset(&mut self) {
   224→        self.attempt = 0;
   225→    }
   226→
   227→    /// Get current attempt number
   228→    pub fn attempt(&self) -> u32 {
   229→        self.attempt
   230→    }
   231→
   232→    /// Check if max attempts reached
   233→    pub fn is_exhausted(&self) -> bool {
   234→        self.max_attempts > 0 && self.attempt >= self.max_attempts
   235→    }
   236→}
