   238→/// Health check metrics for a relay connection
   239→#[derive(Debug, Clone)]
   240→pub struct HealthMetrics {
   241→    /// Connection URL
   242→    pub url: String,
   243→    /// Last successful ping timestamp
   244→    pub last_ping: Option<Instant>,
   245→    /// Ping round-trip time (ms)
   246→    pub ping_rtt_ms: Option<u32>,
   247→    /// Total successful messages
   248→    pub successful_messages: u64,
   249→    /// Total failed messages
   250→    pub failed_messages: u64,
   251→    /// Circuit breaker state
   252→    pub circuit_state: CircuitState,
   253→    /// Current backoff attempt
   254→    pub backoff_attempt: u32,
   255→    /// Connection uptime duration
   256→    pub uptime: Option<Duration>,
   257→    /// Last error message
   258→    pub last_error: Option<String>,
   259→}
   260→
   261→impl HealthMetrics {
   262→    /// Create new health metrics
   263→    pub fn new(url: impl Into<String>) -> Self {
   264→        Self {
   265→            url: url.into(),
   266→            last_ping: None,
   267→            ping_rtt_ms: None,
   268→            successful_messages: 0,
   269→            failed_messages: 0,
   270→            circuit_state: CircuitState::Closed,
   271→            backoff_attempt: 0,
   272→            uptime: None,
   273→            last_error: None,
   274→        }
   275→    }
   276→
   277→    /// Calculate success rate (0.0 to 1.0)
   278→    pub fn success_rate(&self) -> f32 {
   279→        let total = self.successful_messages + self.failed_messages;
   280→        if total == 0 {
   281→            return 1.0;
   282→        }
   283→        self.successful_messages as f32 / total as f32
   284→    }
   285→
   286→    /// Check if connection is healthy
   287→    ///
   288→    /// Healthy = success rate > 80% AND (last ping < 60s OR no pings yet)
   289→    pub fn is_healthy(&self) -> bool {
   290→        let rate_ok = self.success_rate() > 0.8;
   291→        let ping_ok = self.last_ping
   292→            .map(|t| t.elapsed() < Duration::from_secs(60))
   293→            .unwrap_or(true); // No ping yet is OK
   294→
   295→        rate_ok && ping_ok && self.circuit_state == CircuitState::Closed
   296→    }
   297→
   298→    /// Get health status as string
   299→    pub fn status(&self) -> &'static str {
   300→        if self.is_healthy() {
   301→            "healthy"
   302→        } else if self.circuit_state == CircuitState::Open {
   303→            "circuit_open"
   304→        } else if self.success_rate() < 0.5 {
   305→            "degraded"
   306→        } else {
   307→            "unhealthy"
   308→        }
   309→    }
   310→}
