     1→//! APM (Actions Per Minute) calculation module
     2→//!
     3→//! Measures agent velocity and effectiveness using the formula:
     4→//! APM = (messages + tool_calls) / duration_minutes
     5→//!
     6→//! This metric enables comparison between:
     7→//! - Interactive Claude Code usage (~4.5 APM)
     8→//! - Autonomous Autopilot runs (~19 APM)
     9→
    10→use chrono::{DateTime, Duration, Utc};
    11→use serde::{Deserialize, Serialize};
    12→
    13→/// APM statistics for various time windows
    14→#[derive(Debug, Clone, Serialize, Deserialize)]
    15→pub struct APMStats {
    16→    /// APM for current session only
    17→    pub session: Option<f64>,
    18→    /// APM for last 1 hour
    19→    pub last_1h: Option<f64>,
    20→    /// APM for last 6 hours
    21→    pub last_6h: Option<f64>,
    22→    /// APM for last 24 hours
    23→    pub last_1d: Option<f64>,
    24→    /// APM for last 7 days
    25→    pub last_1w: Option<f64>,
    26→    /// APM for last 30 days
    27→    pub last_1m: Option<f64>,
    28→    /// APM for all recorded data
    29→    pub lifetime: Option<f64>,
    30→}
    31→
    32→impl Default for APMStats {
    33→    fn default() -> Self {
    34→        Self {
    35→            session: None,
    36→            last_1h: None,
    37→            last_6h: None,
    38→            last_1d: None,
    39→            last_1w: None,
    40→            last_1m: None,
    41→            lifetime: None,
    42→        }
    43→    }
    44→}
    45→
    46→/// APM snapshot for a specific time period
    47→#[derive(Debug, Clone, Serialize, Deserialize)]
    48→pub struct APMSnapshot {
    49→    pub timestamp: DateTime<Utc>,
    50→    pub source: APMSource,
    51→    pub window: APMWindow,
    52→    pub apm: f64,
    53→    pub actions: u32,
    54→    pub duration_minutes: f64,
    55→    pub messages: u32,
    56→    pub tool_calls: u32,
    57→}
    58→
    59→/// Source of APM data
    60→#[derive(Debug, Clone, Copy, Serialize, Deserialize, PartialEq, Eq)]
    61→#[serde(rename_all = "snake_case")]
    62→pub enum APMSource {
    63→    /// Data from autopilot autonomous runs
    64→    Autopilot,
    65→    /// Data from interactive Claude Code sessions
    66→    ClaudeCode,
    67→    /// Combined data from both sources
    68→    Combined,
    69→}
    70→
    71→impl APMSource {
    72→    pub fn as_str(&self) -> &str {
    73→        match self {
    74→            Self::Autopilot => "autopilot",
    75→            Self::ClaudeCode => "claude_code",
    76→            Self::Combined => "combined",
    77→        }
    78→    }
    79→}
    80→
    81→/// Time window for APM calculation
    82→#[derive(Debug, Clone, Copy, Serialize, Deserialize, PartialEq, Eq)]
    83→#[serde(rename_all = "snake_case")]
    84→pub enum APMWindow {
    85→    /// Current session only
    86→    Session,
    87→    /// Last 1 hour
    88→    Hour1,
    89→    /// Last 6 hours
    90→    Hour6,
    91→    /// Last 24 hours
    92→    Day1,
    93→    /// Last 7 days
    94→    Week1,
    95→    /// Last 30 days
    96→    Month1,
    97→    /// All recorded data
    98→    Lifetime,
    99→}
   100→
   101→impl APMWindow {
   102→    pub fn as_str(&self) -> &str {
   103→        match self {
   104→            Self::Session => "session",
   105→            Self::Hour1 => "1h",
   106→            Self::Hour6 => "6h",
   107→            Self::Day1 => "1d",
   108→            Self::Week1 => "1w",
   109→            Self::Month1 => "1m",
   110→            Self::Lifetime => "lifetime",
   111→        }
   112→    }
   113→
   114→    /// Get the duration for this window (None for Session and Lifetime)
   115→    pub fn duration(&self) -> Option<Duration> {
   116→        match self {
   117→            Self::Session | Self::Lifetime => None,
   118→            Self::Hour1 => Some(Duration::hours(1)),
   119→            Self::Hour6 => Some(Duration::hours(6)),
   120→            Self::Day1 => Some(Duration::hours(24)),
   121→            Self::Week1 => Some(Duration::days(7)),
   122→            Self::Month1 => Some(Duration::days(30)),
   123→        }
   124→    }
   125→}
   126→
   127→/// APM performance tier based on APM value
   128→#[derive(Debug, Clone, Copy, PartialEq, Eq)]
   129→pub enum APMTier {
   130→    /// 0-5 APM: Baseline performance
   131→    Baseline,
   132→    /// 5-15 APM: Active work
   133→    Active,
   134→    /// 15-30 APM: Productive performance
   135→    Productive,
   136→    /// 30-50 APM: High performance
   137→    HighPerformance,
   138→    /// 50+ APM: Elite performance
   139→    Elite,
   140→}
   141→
   142→impl APMTier {
   143→    /// Get the tier for a given APM value
   144→    pub fn from_apm(apm: f64) -> Self {
   145→        if apm < 5.0 {
   146→            Self::Baseline
   147→        } else if apm < 15.0 {
   148→            Self::Active
   149→        } else if apm < 30.0 {
   150→            Self::Productive
   151→        } else if apm < 50.0 {
   152→            Self::HighPerformance
   153→        } else {
   154→            Self::Elite
   155→        }
   156→    }
   157→
   158→    /// Get the color for this tier (for terminal/UI display)
   159→    pub fn color(&self) -> &str {
   160→        match self {
   161→            Self::Baseline => "gray",
   162→            Self::Active => "blue",
   163→            Self::Productive => "green",
   164→            Self::HighPerformance => "amber",
   165→            Self::Elite => "gold",
   166→        }
   167→    }
   168→
   169→    /// Get the name of this tier
   170→    pub fn name(&self) -> &str {
   171→        match self {
   172→            Self::Baseline => "Baseline",
   173→            Self::Active => "Active",
   174→            Self::Productive => "Productive",
   175→            Self::HighPerformance => "High Performance",
   176→            Self::Elite => "Elite",
   177→        }
   178→    }
   179→}
   180→
   181→/// Calculate APM from raw metrics
   182→///
   183→/// # Arguments
   184→/// * `messages` - Total number of user and assistant messages
   185→/// * `tool_calls` - Total number of tool invocations
   186→/// * `duration_minutes` - Wall-clock duration in minutes
   187→///
   188→/// # Returns
   189→/// APM value or None if duration is zero/negative
   190→pub fn calculate_apm(messages: u32, tool_calls: u32, duration_minutes: f64) -> Option<f64> {
   191→    if duration_minutes <= 0.0 {
   192→        return None;
   193→    }
   194→
   195→    let actions = messages + tool_calls;
   196→    Some(actions as f64 / duration_minutes)
   197→}
   198→
   199→/// Calculate APM from timestamps
   200→///
   201→/// # Arguments
   202→/// * `messages` - Total number of messages
   203→/// * `tool_calls` - Total number of tool calls
   204→/// * `start_time` - Start timestamp
   205→/// * `end_time` - End timestamp
   206→///
   207→/// # Returns
   208→/// APM value or None if duration is invalid
   209→pub fn calculate_apm_from_timestamps(
   210→    messages: u32,
   211→    tool_calls: u32,
   212→    start_time: DateTime<Utc>,
   213→    end_time: DateTime<Utc>,
   214→) -> Option<f64> {
   215→    let duration = end_time.signed_duration_since(start_time);
   216→    let duration_minutes = duration.num_milliseconds() as f64 / 60_000.0;
   217→
   218→    calculate_apm(messages, tool_calls, duration_minutes)
   219→}
   220→
   221→/// Session data for APM calculation
   222→#[derive(Debug, Clone)]
   223→pub struct SessionData {
   224→    pub source: APMSource,
   225→    pub start_time: DateTime<Utc>,
   226→    pub end_time: DateTime<Utc>,
   227→    pub messages: u32,
   228→    pub tool_calls: u32,
   229→}
   230→
   231→impl SessionData {
   232→    /// Calculate APM for this session
   233→    pub fn apm(&self) -> Option<f64> {
   234→        calculate_apm_from_timestamps(
   235→            self.messages,
   236→            self.tool_calls,
   237→            self.start_time,
   238→            self.end_time,
   239→        )
   240→    }
   241→
   242→    /// Get total actions (messages + tool_calls)
   243→    pub fn actions(&self) -> u32 {
   244→        self.messages + self.tool_calls
   245→    }
   246→
   247→    /// Get duration in minutes
   248→    pub fn duration_minutes(&self) -> f64 {
   249→        let duration = self.end_time.signed_duration_since(self.start_time);
   250→        duration.num_milliseconds() as f64 / 60_000.0
   251→    }
   252→}
   253→
   254→#[cfg(test)]
   255→mod tests {
   256→    use super::*;
   257→
   258→    #[test]
   259→    fn test_calculate_apm() {
   260→        // 50 messages + 150 tool_calls = 200 actions over 10 minutes = 20 APM
   261→        let apm = calculate_apm(50, 150, 10.0).unwrap();
   262→        assert_eq!(apm, 20.0);
   263→    }
   264→
   265→    #[test]
   266→    fn test_calculate_apm_zero_duration() {
   267→        assert!(calculate_apm(50, 150, 0.0).is_none());
   268→    }
   269→
   270→    #[test]
   271→    fn test_calculate_apm_negative_duration() {
   272→        assert!(calculate_apm(50, 150, -5.0).is_none());
   273→    }
   274→
   275→    #[test]
   276→    fn test_apm_tier_baseline() {
   277→        assert_eq!(APMTier::from_apm(0.0), APMTier::Baseline);
   278→        assert_eq!(APMTier::from_apm(4.9), APMTier::Baseline);
   279→    }
   280→
   281→    #[test]
   282→    fn test_apm_tier_active() {
   283→        assert_eq!(APMTier::from_apm(5.0), APMTier::Active);
   284→        assert_eq!(APMTier::from_apm(10.0), APMTier::Active);
   285→        assert_eq!(APMTier::from_apm(14.9), APMTier::Active);
   286→    }
   287→
   288→    #[test]
   289→    fn test_apm_tier_productive() {
   290→        assert_eq!(APMTier::from_apm(15.0), APMTier::Productive);
   291→        assert_eq!(APMTier::from_apm(20.0), APMTier::Productive);
   292→        assert_eq!(APMTier::from_apm(29.9), APMTier::Productive);
   293→    }
   294→
   295→    #[test]
   296→    fn test_apm_tier_high_performance() {
   297→        assert_eq!(APMTier::from_apm(30.0), APMTier::HighPerformance);
   298→        assert_eq!(APMTier::from_apm(40.0), APMTier::HighPerformance);
   299→        assert_eq!(APMTier::from_apm(49.9), APMTier::HighPerformance);
   300→    }
   301→
   302→    #[test]
   303→    fn test_apm_tier_elite() {
   304→        assert_eq!(APMTier::from_apm(50.0), APMTier::Elite);
   305→        assert_eq!(APMTier::from_apm(100.0), APMTier::Elite);
   306→    }
   307→
   308→    #[test]
   309→    fn test_session_data_apm() {
   310→        let start = Utc::now();
   311→        let end = start + Duration::minutes(10);
   312→
   313→        let session = SessionData {
   314→            source: APMSource::Autopilot,
   315→            start_time: start,
   316→            end_time: end,
   317→            messages: 50,
   318→            tool_calls: 150,
   319→        };
   320→
   321→        assert_eq!(session.actions(), 200);
   322→        assert!((session.duration_minutes() - 10.0).abs() < 0.001);
   323→        assert_eq!(session.apm().unwrap(), 20.0);
   324→    }
   325→
   326→    #[test]
   327→    fn test_apm_source_as_str() {
   328→        assert_eq!(APMSource::Autopilot.as_str(), "autopilot");
   329→        assert_eq!(APMSource::ClaudeCode.as_str(), "claude_code");
   330→        assert_eq!(APMSource::Combined.as_str(), "combined");
   331→    }
   332→
   333→    #[test]
   334→    fn test_apm_window_as_str() {
   335→        assert_eq!(APMWindow::Session.as_str(), "session");
   336→        assert_eq!(APMWindow::Hour1.as_str(), "1h");
   337→        assert_eq!(APMWindow::Hour6.as_str(), "6h");
   338→        assert_eq!(APMWindow::Day1.as_str(), "1d");
   339→        assert_eq!(APMWindow::Week1.as_str(), "1w");
   340→        assert_eq!(APMWindow::Month1.as_str(), "1m");
   341→        assert_eq!(APMWindow::Lifetime.as_str(), "lifetime");
   342→    }
   343→}
