    28→    debug!("Current schema version: {}", version);
    29→
    30→    // Run migrations
    31→    if version < 1 {
    32→        migrate_v1(&conn)?;
    33→    }
    34→    if version < 2 {
    35→        migrate_v2(&conn)?;
    36→    }
    37→    if version < 3 {
    38→        migrate_v3(&conn)?;
    39→    }
