    81→
    82→fn get_schema_version(conn: &Connection) -> Result<i32> {
    83→    // Create version table if it doesn't exist
    84→    conn.execute(
    85→        "CREATE TABLE IF NOT EXISTS schema_version (version INTEGER NOT NULL)",
    86→        [],
    87→    )?;
    88→
    89→    let version: Option<i32> = conn
    90→        .query_row("SELECT version FROM schema_version LIMIT 1", [], |row| {
    91→            row.get(0)
    92→        })
    93→        .ok();
    94→
    95→    Ok(version.unwrap_or(0))
    96→}
    97→
    98→fn set_schema_version(conn: &Connection, version: i32) -> Result<()> {
    99→    debug!("Setting schema version to {}", version);
   100→    conn.execute("DELETE FROM schema_version", [])?;
   101→    conn.execute("INSERT INTO schema_version (version) VALUES (?)", [version])?;
   102→    Ok(())
   103→}
   104→
   105→fn migrate_v1(conn: &Connection) -> Result<()> {
   106→    info!("Running migration v1");
   107→    conn.execute_batch(
   108→        r#"
   109→        -- Issues table
   110→        CREATE TABLE IF NOT EXISTS issues (
   111→            id TEXT PRIMARY KEY,
   112→            number INTEGER NOT NULL,
   113→            title TEXT NOT NULL,
   114→            description TEXT,
   115→            status TEXT NOT NULL DEFAULT 'open',
   116→            priority TEXT DEFAULT 'medium',
   117→            issue_type TEXT DEFAULT 'task',
   118→            is_blocked INTEGER DEFAULT 0,
   119→            blocked_reason TEXT,
   120→            claimed_by TEXT,
   121→            claimed_at TEXT,
   122→            created_at TEXT NOT NULL,
   123→            updated_at TEXT NOT NULL,
   124→            completed_at TEXT
   125→        );
   126→
   127→        CREATE INDEX IF NOT EXISTS idx_issues_status ON issues(status);
   128→        CREATE INDEX IF NOT EXISTS idx_issues_number ON issues(number);
   129→
   130→        -- Issue events table (audit log)
   131→        CREATE TABLE IF NOT EXISTS issue_events (
   132→            id TEXT PRIMARY KEY,
   133→            issue_id TEXT NOT NULL REFERENCES issues(id),
   134→            actor TEXT,
   135→            event_type TEXT NOT NULL,
   136→            old_value TEXT,
   137→            new_value TEXT,
   138→            created_at TEXT NOT NULL
   139→        );
   140→
   141→        CREATE INDEX IF NOT EXISTS idx_issue_events_issue ON issue_events(issue_id);
   142→
   143→        -- Issue counter for sequential numbering
   144→        CREATE TABLE IF NOT EXISTS issue_counter (
   145→            id INTEGER PRIMARY KEY CHECK (id = 1),
   146→            next_number INTEGER NOT NULL DEFAULT 1
   147→        );
   148→
   149→        INSERT OR IGNORE INTO issue_counter (id, next_number) VALUES (1, 1);
   150→        "#,
   151→    )?;
   152→
   153→    set_schema_version(conn, 1).map_err(|e| {
   154→        error!("Failed to set schema version to 1: {}", e);
   155→        e
   156→    })?;
   157→    info!("Migration v1 completed successfully");
   158→    Ok(())
   159→}
   160→
   161→fn migrate_v2(conn: &Connection) -> Result<()> {
   162→    info!("Running migration v2");
   163→    // Clean up any NULL ids (from manual inserts) and delete those rows
   164→    conn.execute("DELETE FROM issues WHERE id IS NULL OR id = ''", []).map_err(|e| {
   165→        error!("Failed to clean up NULL issue ids: {}", e);
   166→        e
   167→    })?;
   168→    conn.execute("DELETE FROM issue_events WHERE id IS NULL OR id = ''", []).map_err(|e| {
   169→        error!("Failed to clean up NULL event ids: {}", e);
   170→        e
   171→    })?;
   172→
   173→    // Recreate issues table with explicit NOT NULL constraint on id
   174→    conn.execute_batch(
   175→        r#"
   176→        -- Create new table with proper constraints
   177→        CREATE TABLE issues_new (
   178→            id TEXT NOT NULL PRIMARY KEY,
   179→            number INTEGER NOT NULL UNIQUE,
   180→            title TEXT NOT NULL,
   181→            description TEXT,
   182→            status TEXT NOT NULL DEFAULT 'open',
   183→            priority TEXT DEFAULT 'medium',
   184→            issue_type TEXT DEFAULT 'task',
   185→            is_blocked INTEGER DEFAULT 0,
   186→            blocked_reason TEXT,
   187→            claimed_by TEXT,
   188→            claimed_at TEXT,
   189→            created_at TEXT NOT NULL,
   190→            updated_at TEXT NOT NULL,
   191→            completed_at TEXT,
   192→            CHECK(id != '')
   193→        );
   194→
   195→        -- Copy data from old table
   196→        INSERT INTO issues_new SELECT * FROM issues WHERE id IS NOT NULL AND id != '';
   197→
   198→        -- Drop old table
   199→        DROP TABLE issues;
   200→

