    47→
    48→/// Callback invoked when session_id becomes available
    49→pub type SessionIdCallback = Box<dyn FnOnce(&str) + Send>;
    50→
    51→/// Collects SdkMessages into a Trajectory
    52→///
    53→/// The TrajectoryCollector processes Claude Agent SDK messages and builds
    54→/// a complete trajectory of an agent's execution, including tool calls,
    55→/// thinking blocks, and results. It supports streaming output to both
    56→/// rlog (human-readable) and JSONL (Claude Code compatible) formats.
    57→///
    58→/// # Examples
    59→///
    60→/// Basic usage:
    61→///
    62→/// ```no_run
    63→/// use autopilot::TrajectoryCollector;
    64→///
    65→/// let mut collector = TrajectoryCollector::new(
    66→///     "Fix the bug in auth.rs".to_string(),
    67→///     "claude-sonnet-4-5-20250929".to_string(),
    68→///     "/home/user/project".to_string(),
    69→///     "abc123".to_string(),
    70→///     Some("main".to_string()),
    71→/// );
    72→///
    73→/// // Enable streaming output
    74→/// collector.enable_streaming("logs/session.rlog").unwrap();
    75→/// collector.enable_jsonl_streaming("logs/session.jsonl").unwrap();
    76→///
    77→/// // Process SDK messages as they arrive
    78→/// // for msg in sdk_stream {
    79→/// //     collector.process_message(&msg);
    80→/// // }
    81→///
    82→/// // Get the final trajectory
    83→/// let trajectory = collector.finish();
    84→/// println!("Session completed: {}", trajectory.session_id);
    85→/// ```
    86→pub struct TrajectoryCollector {
    87→    trajectory: Trajectory,
    88→    /// Optional rlog writer for streaming output (truncated, human-readable)
    89→    rlog_writer: Option<RlogWriter>,
    90→    /// Path to the rlog file for header updates
    91→    rlog_path: Option<std::path::PathBuf>,
    92→    /// Optional JSONL writer for full data capture (untruncated, Claude Code compatible)
    93→    jsonl_writer: Option<JsonlWriter>,
    94→    /// Callback invoked when session_id is set
    95→    session_id_callback: Option<SessionIdCallback>,
    96→    /// Active subagents: tool_id -> (agent_type, description)
    97→    active_subagents: std::collections::HashMap<String, (String, String)>,
    98→}
    99→
   100→impl TrajectoryCollector {
   101→    /// Create a new collector
   102→    pub fn new(
   103→        prompt: String,
   104→        model: String,
   105→        cwd: String,
   106→        repo_sha: String,
   107→        branch: Option<String>,
   108→    ) -> Self {
   109→        Self {
   110→            trajectory: Trajectory::new(prompt, model, cwd, repo_sha, branch),
   111→            rlog_writer: None,
   112→            rlog_path: None,
   113→            jsonl_writer: None,
   114→            session_id_callback: None,
   115→            active_subagents: std::collections::HashMap::new(),
   116→        }
   117→    }
   118→
   119→    /// Set a callback to be invoked when session_id becomes available
   120→    ///
   121→    /// This is useful for tasks that need to know the session ID as soon as
   122→    /// it's assigned by the SDK, such as updating databases or creating
   123→    /// trajectory links.
   124→    ///
   125→    /// # Examples
   126→    ///
   127→    /// ```no_run
   128→    /// use autopilot::TrajectoryCollector;
   129→    ///
   130→    /// let mut collector = TrajectoryCollector::new(
   131→    ///     "Task".to_string(),
   132→    ///     "claude-sonnet-4-5-20250929".to_string(),
   133→    ///     "/home/user/project".to_string(),
   134→    ///     "abc123".to_string(),
   135→    ///     None,
   136→    /// );
   137→    ///
   138→    /// collector.on_session_id(|session_id| {
   139→    ///     println!("Session started: {}", session_id);
   140→    ///     // Update database, send notification, etc.
   141→    /// });
   142→    /// ```
   143→    pub fn on_session_id<F>(&mut self, callback: F)
   144→    where
   145→        F: FnOnce(&str) + Send + 'static,
   146→    {
   147→        self.session_id_callback = Some(Box::new(callback));
   148→    }
   149→
   150→    /// Enable streaming rlog output to a file
   151→    pub fn enable_streaming(&mut self, path: impl AsRef<std::path::Path>) -> std::io::Result<()> {
   152→        let path = path.as_ref();
   153→        let mut writer = RlogWriter::new_streaming(path)?;
   154→        writer.write_header(&self.trajectory)?;
   155→        self.rlog_writer = Some(writer);
   156→        self.rlog_path = Some(path.to_path_buf());
   157→        Ok(())
   158→    }
   159→
   160→    /// Enable JSONL streaming for full data capture (Claude Code compatible format)
   161→    pub fn enable_jsonl_streaming(&mut self, path: impl AsRef<std::path::Path>) -> std::io::Result<()> {
   162→        let mut writer = JsonlWriter::new();
   163→        writer.init(path, &self.trajectory.session_id)?;
   164→        self.jsonl_writer = Some(writer);
   165→        Ok(())
   166→    }
   167→
   168→    /// Set the session_id (useful when resuming a session)
   169→    pub fn set_session_id(&mut self, session_id: String) {
   170→        self.trajectory.session_id = session_id;
   171→    }
   172→
   173→    /// Stream the last added step to rlog file (if streaming is enabled)
   174→    fn stream_last_step(&mut self) {
   175→        if let (Some(writer), Some(last_step)) = (&mut self.rlog_writer, self.trajectory.steps.last()) {
   176→            let _ = writer.append_step(last_step);
   177→        }
   178→    }
   179→
   180→    /// Stream a raw SDK message to JSONL file (if JSONL streaming is enabled)
   181→    fn stream_message_to_jsonl(&mut self, msg: &SdkMessage) {
   182→        if let Some(writer) = &mut self.jsonl_writer {
   183→            // Convert SdkMessage to a serializable format
   184→            let json_value = match msg {
   185→                SdkMessage::System(sys) => {
   186→                    serde_json::json!({
   187→                        "type": "system",
   188→                        "message": sys,
   189→                        "timestamp": chrono::Utc::now().to_rfc3339()
   190→                    })
   191→                }
   192→                SdkMessage::Assistant(asst) => {
   193→                    serde_json::json!({
   194→                        "type": "assistant",
   195→                        "message": asst.message,
   196→                        "timestamp": chrono::Utc::now().to_rfc3339()
   197→                    })
   198→                }
   199→                SdkMessage::User(user) => {
   200→                    serde_json::json!({
   201→                        "type": "user",
   202→                        "message": user.message,
   203→                        "timestamp": chrono::Utc::now().to_rfc3339()
   204→                    })
   205→                }
   206→                SdkMessage::Result(result) => {
   207→                    serde_json::json!({
   208→                        "type": "result",
   209→                        "message": result,
   210→                        "timestamp": chrono::Utc::now().to_rfc3339()
   211→                    })
   212→                }
   213→                SdkMessage::ToolProgress(progress) => {
   214→                    serde_json::json!({
   215→                        "type": "tool_progress",
   216→                        "message": progress,
   217→                        "timestamp": chrono::Utc::now().to_rfc3339()
   218→                    })
   219→                }
   220→                SdkMessage::StreamEvent(event) => {
   221→                    serde_json::json!({
   222→                        "type": "stream_event",
   223→                        "message": event,
   224→                        "timestamp": chrono::Utc::now().to_rfc3339()
   225→                    })
   226→                }
   227→                SdkMessage::AuthStatus(auth) => {
   228→                    serde_json::json!({
   229→                        "type": "auth_status",
   230→                        "message": auth,
   231→                        "timestamp": chrono::Utc::now().to_rfc3339()
   232→                    })
   233→                }
   234→            };
   235→            let _ = writer.write_value(&json_value);
   236→        }
   237→    }
   238→
   239→    /// Process an SdkMessage and add to trajectory
   240→    pub fn process_message(&mut self, msg: &SdkMessage) {
   241→        // Stream raw message to JSONL first (full data capture)
   242→        self.stream_message_to_jsonl(msg);
   243→
   244→        // Then process into trajectory steps (for rlog and analysis)
   245→        match msg {
   246→            SdkMessage::System(sys) => self.process_system(sys),
   247→            SdkMessage::Assistant(asst) => self.process_assistant(asst),
   248→            SdkMessage::User(user) => self.process_user(user),
   249→            SdkMessage::Result(result) => self.process_result(result),
   250→            SdkMessage::ToolProgress(_) => { /* Could track progress */ }
   251→            SdkMessage::StreamEvent(_) => { /* Could track streaming */ }
   252→            SdkMessage::AuthStatus(_) => { /* Could track auth */ }
   253→        }
   254→    }
   255→
   256→    fn process_system(&mut self, sys: &SdkSystemMessage) {
   257→        match sys {
   258→            SdkSystemMessage::Init(init) => {
   259→                self.trajectory.session_id = init.session_id.clone();
   260→                self.trajectory.add_step(StepType::SystemInit {
   261→                    model: init.model.clone(),
   262→                });
   263→
   264→                // Update the header now that we have the session_id
   265→                if let (Some(writer), Some(path)) = (&mut self.rlog_writer, &self.rlog_path) {
   266→                    let _ = writer.update_header(path, &self.trajectory);
   267→                }
   268→
   269→                // Invoke session_id callback if set
   270→                if let Some(callback) = self.session_id_callback.take() {
   271→                    callback(&init.session_id);
   272→                }
   273→
   274→                self.stream_last_step();
   275→            }
   276→            SdkSystemMessage::Status(status) => {
   277→                if let Some(s) = &status.status {
   278→                    self.trajectory.add_step(StepType::SystemStatus {
   279→                        status: format!("{:?}", s),
   280→                    });
   281→                    self.stream_last_step();
   282→                }
   283→            }
   284→            _ => {}
   285→        }
   286→    }
   287→
   288→    fn process_assistant(&mut self, asst: &SdkAssistantMessage) {
   289→        // Extract token info before adding steps (to avoid borrow issues)
   290→        let (tokens_in, tokens_out, tokens_cached) = Self::extract_tokens(&asst.message);
   291→
   292→        // Parse the message content which contains content blocks
   293→        if let Some(content) = asst.message.get("content").and_then(|c| c.as_array()) {
   294→            for block in content {
   295→                let block_type = block.get("type").and_then(|t| t.as_str()).unwrap_or("");
   296→
   297→                match block_type {
   298→                    "thinking" => {
   299→                        let text = block
   300→                            .get("thinking")
   301→                            .and_then(|t| t.as_str())
   302→                            .unwrap_or("");
   303→                        let sig = block
   304→                            .get("signature")
   305→                            .and_then(|s| s.as_str())
   306→                            .map(String::from);
   307→                        let step = self.trajectory.add_step(StepType::Thinking {
   308→                            content: text.to_string(),
