   397→    fn extract_tokens(message: &Value) -> (Option<u64>, Option<u64>, Option<u64>) {
   398→        if let Some(usage) = message.get("usage") {
   399→            (
   400→                usage.get("input_tokens").and_then(|t| t.as_u64()),
   401→                usage.get("output_tokens").and_then(|t| t.as_u64()),
   402→                usage.get("cache_read_input_tokens").and_then(|t| t.as_u64()),
   403→            )
   404→        } else {
   405→            (None, None, None)
