   411→        if let Some(content) = user.message.get("content") {
   412→            match content {
   413→                Value::Array(arr) => {
   414→                    // Handle tool_result blocks
   415→                    for block in arr {
   416→                        if block.get("type").and_then(|t| t.as_str()) == Some("tool_result") {
   417→                            let tool_id = block
   418→                                .get("tool_use_id")
   419→                                .and_then(|i| i.as_str())
   420→                                .unwrap_or("");
