   422→                                .get("is_error")
   423→                                .and_then(|e| e.as_bool())
   424→                                .unwrap_or(false);
   425→                            let output = self.extract_tool_result_content(block);
   426→
   427→                            // Check if this is a subagent completion
   428→                            if let Some((agent_type, _)) = self.active_subagents.remove(tool_id) {
   429→                                // Extract summary from tool result
   430→                                let summary = if output.is_empty() {
   431→                                    None
   432→                                } else {
   433→                                    // Truncate summary to first 200 chars for readability
   434→                                    let trunc = if output.len() > 200 {
   435→                                        format!("{}...", &output[..200])
   436→                                    } else {
   437→                                        output.clone()
   438→                                    };
   439→                                    Some(trunc)
   440→                                };
   441→
   442→                                // Add subagent completion step
   443→                                self.trajectory.add_step(StepType::Subagent {
   444→                                    agent_id: tool_id.to_string(),
   445→                                    agent_type,
   446→                                    status: if is_error {
   447→                                        SubagentStatus::Error
   448→                                    } else {
   449→                                        SubagentStatus::Done
   450→                                    },
   451→                                    summary,
   452→                                });
   453→                                self.stream_last_step();
   454→                            }
   455→
   456→                            self.trajectory.add_step(StepType::ToolResult {
   457→                                tool_id: tool_id.to_string(),
   458→                                success: !is_error,
   459→                                output: if output.is_empty() {
   460→                                    None
   461→                                } else {
   462→                                    Some(output)
   463→                                },
   464→                            });
   465→                            self.stream_last_step();
   466→                        }
   467→                    }
   468→                }
   469→                Value::String(s) => {
   470→                    // Plain user message
   471→                    self.trajectory.add_step(StepType::User {
   472→                        content: s.clone(),
   473→                    });
   474→                    self.stream_last_step();
   475→                }
   476→                _ => {}
   477→            }
   478→        }
   479→    }
