   481→    fn process_result(&mut self, result: &SdkResultMessage) {
   482→        self.trajectory.ended_at = Some(Utc::now());
   483→
   484→        match result {
   485→            SdkResultMessage::Success(success) => {
   486→                self.trajectory.usage = TokenUsage {
   487→                    input_tokens: success.usage.input_tokens,
   488→                    output_tokens: success.usage.output_tokens,
   489→                    cache_read_tokens: success.usage.cache_read_input_tokens.unwrap_or(0),
   490→                    cache_creation_tokens: success.usage.cache_creation_input_tokens.unwrap_or(0),
   491→                    cost_usd: success.total_cost_usd,
   492→                };
   493→
   494→                // Calculate APM for the trajectory
   495→                let apm = self.calculate_trajectory_apm();
   496→
   497→                self.trajectory.result = Some(TrajectoryResult {
   498→                    success: !success.is_error,
   499→                    duration_ms: success.duration_ms,
   500→                    num_turns: success.num_turns,
   501→                    result_text: Some(success.result.clone()),
   502→                    errors: Vec::new(),
   503→                    issues_completed: 0,
   504→                    apm,
   505→                });
   506→            }
   507→            SdkResultMessage::ErrorDuringExecution(err)
   508→            | SdkResultMessage::ErrorMaxTurns(err)
   509→            | SdkResultMessage::ErrorMaxBudget(err)
   510→            | SdkResultMessage::ErrorMaxStructuredOutputRetries(err) => {
   511→                self.trajectory.usage = TokenUsage {
   512→                    input_tokens: err.usage.input_tokens,
   513→                    output_tokens: err.usage.output_tokens,
   514→                    cache_read_tokens: err.usage.cache_read_input_tokens.unwrap_or(0),
   515→                    cache_creation_tokens: err.usage.cache_creation_input_tokens.unwrap_or(0),
   516→                    cost_usd: err.total_cost_usd,
   517→                };
   518→
   519→                // Calculate APM for the trajectory
   520→                let apm = self.calculate_trajectory_apm();
   521→
   522→                self.trajectory.result = Some(TrajectoryResult {
   523→                    success: false,
   524→                    duration_ms: err.duration_ms,
   525→                    num_turns: err.num_turns,
   526→                    result_text: None,
   527→                    errors: err.errors.clone(),
   528→                    issues_completed: 0,
   529→                    apm,
   530→                });
   531→            }
   532→        }
   533→    }
   534→
   535→    /// Calculate APM (Actions Per Minute) for the current trajectory
   536→    fn calculate_trajectory_apm(&self) -> Option<f64> {
   537→        use crate::apm::calculate_apm_from_timestamps;
   538→
   539→        // Count messages (user + assistant)
   540→        let messages = self.trajectory.steps.iter()
   541→            .filter(|s| matches!(&s.step_type, StepType::User { .. } | StepType::Assistant { .. }))
   542→            .count() as u32;
   543→
   544→        // Count tool calls
   545→        let tool_calls = self.trajectory.steps.iter()
   546→            .filter(|s| matches!(&s.step_type, StepType::ToolCall { .. }))
   547→            .count() as u32;
   548→
   549→        // Get start and end times
   550→        let start_time = self.trajectory.started_at;
   551→        let end_time = self.trajectory.ended_at.unwrap_or_else(|| Utc::now());
   552→
   553→        calculate_apm_from_timestamps(messages, tool_calls, start_time, end_time)
   554→    }
   555→
   556→    fn extract_tool_result_content(&self, block: &Value) -> String {
   557→        match block.get("content") {
   558→            Some(Value::String(s)) => s.clone(),
   559→            Some(Value::Array(arr)) => arr
   560→                .iter()
   561→                .filter_map(|b| b.get("text").and_then(|t| t.as_str()))
   562→                .collect::<Vec<_>>()
   563→                .join("\n"),
   564→            _ => String::new(),
   565→        }
   566→    }
