   568→    /// Process a Codex ThreadEvent and add to trajectory
   569→    pub fn process_codex_event(&mut self, event: &codex_agent_sdk::ThreadEvent) {
   570→        use codex_agent_sdk::{ThreadEvent, ThreadItemDetails};
   571→
   572→        match event {
   573→            ThreadEvent::ThreadStarted(e) => {
