   611→                                None
   612→                            } else {
   613→                                Some(cmd.aggregated_output.clone())
   614→                            },
   615→                        });
   616→                        self.stream_last_step();
   617→                    }
   618→                    ThreadItemDetails::FileChange(fc) => {
