   728→    /// Finish collecting and return the trajectory
   729→    pub fn finish(mut self) -> Trajectory {
   730→        // Write footer to rlog if streaming is enabled
   731→        if let Some(writer) = &mut self.rlog_writer {
   732→            let _ = writer.write_footer(&self.trajectory);
   733→            let _ = writer.close();
   734→        }
   735→
   736→        // Flush JSONL writer if streaming is enabled
   737→        if let Some(writer) = &mut self.jsonl_writer {
   738→            let _ = writer.flush();
   739→        }
   740→
   741→        self.trajectory
   742→    }
