   754→
   755→/// Extract session_id from a trajectory JSON file.
   756→pub fn extract_session_id_from_json(path: &std::path::Path) -> anyhow::Result<String> {
   757→    let content = std::fs::read_to_string(path)?;
   758→    let traj: trajectory::Trajectory = serde_json::from_str(&content)?;
   759→    if traj.session_id.is_empty() {
   760→        anyhow::bail!("No session_id in trajectory file");
   761→    }
   762→    Ok(traj.session_id)
   763→}
   764→
   765→/// Extract session_id from rlog YAML header.
   766→/// Returns None if the id field is empty or missing.
   767→pub fn extract_session_id_from_rlog(path: &std::path::Path) -> anyhow::Result<Option<String>> {
   768→    let content = std::fs::read_to_string(path)?;
   769→
   770→    // Find YAML header between --- markers
   771→    let lines: Vec<&str> = content.lines().collect();
   772→    if lines.len() < 2 || lines[0] != "---" {
   773→        return Ok(None);
   774→    }
   775→
   776→    // Find end marker
   777→    let end_idx = lines.iter().skip(1).position(|l| *l == "---");
   778→    let end_idx = match end_idx {
   779→        Some(idx) => idx + 1, // +1 because we skipped first line
   780→        None => return Ok(None),
   781→    };
   782→
   783→    // Parse header lines looking for id:
   784→    for line in &lines[1..end_idx] {
   785→        if let Some(rest) = line.strip_prefix("id:") {
   786→            let id = rest.trim();
   787→            if !id.is_empty() {
   788→                return Ok(Some(id.to_string()));
   789→            }
   790→        }
   791→    }
   792→
   793→    Ok(None)
   794→}
   795→
   796→/// Find the workspace root directory by looking for Cargo.toml with `[workspace]`.
   797→///
   798→/// Starts from the current directory and walks up until it finds a Cargo.toml
   799→/// containing `[workspace]`. This ensures autopilot.db is always created at
   800→/// the workspace root, not in subdirectories.
   801→///
   802→/// Returns the current directory if no workspace root is found (fallback).
   803→///
   804→/// # Examples
   805→///
   806→/// ```no_run
   807→/// use autopilot::find_workspace_root;
   808→///
   809→/// let workspace_root = find_workspace_root();
   810→/// println!("Workspace root: {}", workspace_root.display());
   811→///
   812→/// // Use for database path
   813→/// let db_path = workspace_root.join("autopilot.db");
   814→/// ```
   815→pub fn find_workspace_root() -> PathBuf {
   816→    let cwd = std::env::current_dir().unwrap_or_else(|_| PathBuf::from("."));
   817→    let mut current = cwd.as_path();
   818→
   819→    loop {
   820→        let cargo_toml = current.join("Cargo.toml");
   821→        if cargo_toml.exists() {
   822→            if let Ok(content) = std::fs::read_to_string(&cargo_toml) {
   823→                if content.contains("[workspace]") {
   824→                    return current.to_path_buf();
   825→                }
   826→            }
   827→        }
   828→
   829→        match current.parent() {
   830→            Some(parent) => current = parent,
   831→            None => break,
   832→        }
   833→    }
   834→
   835→    // Fallback to current directory if no workspace found
   836→    cwd
   837→}
   838→
   839→/// Get the default path for autopilot.db at the workspace root.
   840→///
   841→/// This ensures the database is always created in a consistent location
   842→/// regardless of which subdirectory the command is run from.
   843→pub fn default_db_path() -> PathBuf {
   844→    find_workspace_root().join("autopilot.db")
   845→}
