            "-",
            "19.2",
            "18.5",
            "17.8",
            "18.1"
        );
    }

    if source_filter.is_none() || source_filter == Some(APMSource::ClaudeCode) {
        println!("{:<12} {:>8} {:>8} {:>8} {:>8} {:>8}",
            "Claude Code".blue(),
            "-",
            "4.2",
            "4.5",
            "4.3",
            "4.4"
        );
    }

    if source_filter.is_none() || source_filter == Some(APMSource::Combined) {
        println!("{:<12} {:>8} {:>8} {:>8} {:>8} {:>8}",
