} else {
    println!("{:<20} {:<40} {:<12} {:<15}", "Name", "Path", "Sessions", "Issues");
    println!("{}", "-".repeat(90));

    let mut total_sessions = 0;
    let mut total_open = 0;
    let mut total_completed = 0;

    for p in projects {
        // Count sessions for this project
        let sessions = session::list_sessions(&conn, Some(&p.id))?;
        let session_count = sessions.len();
        total_sessions += session_count;

        // Count issues for this project
        let issues = issue::list_issues_by_project(&conn, &p.id)?;
        let open_count = issues.iter().filter(|i| i.status == Status::Open).count();
        let completed_count = issues.iter().filter(|i| i.status == Status::Completed).count();
        total_open += open_count;
        total_completed += completed_count;

        let issue_summary = if open_count + completed_count == 0 {
            "-".to_string()
        } else {
            format!("{} / {} done", open_count, completed_count)
        };

        println!(
            "{:<20} {:<40} {:<12} {}",
            p.name,
            p.path,
            session_count,
            issue_summary
        );
    }

    // Print totals
    println!("{}", "-".repeat(90));
    println!(
        "{:<20} {:<40} {:<12} {} / {} done",
        "TOTAL",
        "",
        total_sessions,
        total_open,
        total_completed
    );
}

