    let agent = agent.unwrap_or("claude");

    conn.execute(
        r#"
        INSERT INTO issues (id, number, title, description, priority, issue_type, agent, directive_id, created_at, updated_at)
        VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?)
        "#,
        params![
            id,
            number,
            title,
            description,
            priority.as_str(),
            issue_type.as_str(),
            agent,
            directive_id,
            now,
            now,
        ],
    )?;

    get_issue_by_id(conn, &id)?.ok_or(rusqlite::Error::QueryReturnedNoRows)
}

