   211→    /// Check if provider needs to re-advertise
   212→    pub fn needs_readvertisement(&self) -> bool {
   213→        match self.last_advertised {
   214→            None => true, // Never advertised
   215→            Some(last) => {
   216→                let now = SystemTime::now();
   217→                let elapsed = now.duration_since(last).unwrap_or(Duration::from_secs(0));
   218→                elapsed.as_secs() >= self.config.readvertise_interval_secs
   219→            }
   220→        }
   221→    }
   222→
   223→    /// Create advertisement event
   224→    pub fn create_advertisement(&mut self, pubkey: impl Into<String>) -> HandlerInfo {
   225→        // Update last advertised timestamp
   226→        self.last_advertised = Some(SystemTime::now());
   227→
   228→        // Create HandlerInfo event
   229→        self.config.to_handler_info(pubkey)
   230→    }
   231→
   232→    /// Update provider configuration
   233→    pub fn update_config(&mut self, config: ProviderConfig) {
   234→        self.config = config;
   235→        // Force re-advertisement on config change
   236→        self.last_advertised = None;
   237→    }
