   243→            Some(last) => {
   244→                let now = SystemTime::now();
   245→                let elapsed = now.duration_since(last).unwrap_or(Duration::from_secs(0));
   246→                let interval = Duration::from_secs(self.config.readvertise_interval_secs);
   247→
   248→                if elapsed >= interval {
