     1→//! NIP-89: Application Handlers
     2→//!
     3→//! This NIP defines a way for clients to discover and recommend application handlers
     4→//! (agents, skills, compute providers) based on social graph trust.
     5→//!
     6→//! ## Kinds
     7→//! - 31990: Handler information (advertise capabilities)
     8→//! - 31989: Handler recommendation (social discovery)
     9→//!
    10→//! ## Social Discovery
    11→//! Handler recommendations are weighted by social graph distance:
    12→//! - Direct follows: weight 1.0
    13→//! - Follow-of-follows: weight 0.5
    14→//! - Two degrees separation: weight 0.25
    15→//! - Unknown: weight 0.1
    16→
    17→use serde::{Deserialize, Serialize};
    18→use thiserror::Error;
    19→
    20→/// Kind for handler information
    21→pub const KIND_HANDLER_INFO: u16 = 31990;
    22→
    23→/// Kind for handler recommendation
    24→pub const KIND_HANDLER_RECOMMENDATION: u16 = 31989;
    25→
    26→/// Errors that can occur during NIP-89 operations.
    27→#[derive(Debug, Error)]
    28→pub enum Nip89Error {
    29→    #[error("invalid kind: {0} (expected {1})")]
    30→    InvalidKind(u16, String),
    31→
    32→    #[error("missing required tag: {0}")]
    33→    MissingTag(String),
    34→
    35→    #[error("invalid handler type: {0}")]
    36→    InvalidHandlerType(String),
    37→
    38→    #[error("invalid rating: {0} (expected 1-5)")]
    39→    InvalidRating(u8),
    40→
    41→    #[error("serialization error: {0}")]
    42→    Serialization(String),
    43→}
    44→
    45→/// Type of handler advertised in kind 31990.
    46→#[derive(Debug, Clone, PartialEq, Eq, Serialize, Deserialize)]
    47→#[serde(rename_all = "lowercase")]
    48→pub enum HandlerType {
    49→    /// A skill/capability handler
    50→    Skill,
    51→    /// An autonomous agent
    52→    Agent,
    53→    /// A compute provider
    54→    ComputeProvider,
    55→}
    56→
    57→impl HandlerType {
    58→    pub fn as_str(&self) -> &'static str {
    59→        match self {
    60→            HandlerType::Skill => "skill",
    61→            HandlerType::Agent => "agent",
    62→            HandlerType::ComputeProvider => "compute_provider",
    63→        }
    64→    }
    65→
    66→    pub fn from_str(s: &str) -> Result<Self, Nip89Error> {
    67→        match s.to_lowercase().as_str() {
    68→            "skill" => Ok(HandlerType::Skill),
    69→            "agent" => Ok(HandlerType::Agent),
    70→            "compute_provider" | "computeprovider" => Ok(HandlerType::ComputeProvider),
    71→            _ => Err(Nip89Error::InvalidHandlerType(s.to_string())),
    72→        }
    73→    }
    74→}
