     1→//! Relay integration for marketplace discovery
     2→//!
     3→//! This module provides relay pool management for discovering and fetching
     4→//! marketplace content (skills, data, compute) from Nostr relays.
     5→
     6→use nostr_client::{PoolConfig, RelayPool};
     7→use nostr::{Event, KIND_HANDLER_INFO};
     8→use thiserror::Error;
     9→use tokio::sync::RwLock;
    10→use std::sync::Arc;
    11→
    12→/// Default relays for marketplace discovery
    13→pub const DEFAULT_MARKETPLACE_RELAYS: &[&str] = &[
    14→    "wss://relay.damus.io",
    15→    "wss://relay.primal.net",
    16→    "wss://relay.nostr.band",
    17→    "wss://nos.lol",
    18→];
    19→
    20→/// Errors that can occur during relay operations
    21→#[derive(Debug, Error)]
    22→pub enum RelayError {
    23→    #[error("client error: {0}")]
    24→    Client(String),
    25→
    26→    #[error("connection error: {0}")]
    27→    Connection(String),
    28→
    29→    #[error("subscription error: {0}")]
    30→    Subscription(String),
    31→
    32→    #[error("no relays connected")]
    33→    NoRelays,
    34→
    35→    #[error("timeout waiting for events")]
    36→    Timeout,
    37→}
    38→
    39→impl From<nostr_client::ClientError> for RelayError {
    40→    fn from(err: nostr_client::ClientError) -> Self {
    41→        RelayError::Client(err.to_string())
    42→    }
    43→}
    44→
    45→/// Marketplace relay manager
    46→pub struct MarketplaceRelay {
    47→    pool: Arc<RelayPool>,
    48→    connected: Arc<RwLock<bool>>,
    49→}
    50→
    51→impl MarketplaceRelay {
    52→    /// Create a new marketplace relay manager
    53→    pub fn new() -> Self {
    54→        let config = PoolConfig::default();
    55→        Self {
    56→            pool: Arc::new(RelayPool::new(config)),
    57→            connected: Arc::new(RwLock::new(false)),
    58→        }
    59→    }
    60→
    61→    /// Create with custom pool configuration
    62→    pub fn with_config(config: PoolConfig) -> Self {
    63→        Self {
    64→            pool: Arc::new(RelayPool::new(config)),
    65→            connected: Arc::new(RwLock::new(false)),
    66→        }
    67→    }
    68→
    69→    /// Connect to default marketplace relays
    70→    pub async fn connect(&self) -> Result<(), RelayError> {
    71→        self.connect_to_relays(DEFAULT_MARKETPLACE_RELAYS).await
    72→    }
    73→
    74→    /// Connect to specific relays
    75→    pub async fn connect_to_relays(&self, relays: &[&str]) -> Result<(), RelayError> {
    76→        // Add relays to pool
    77→        for relay in relays {
    78→            self.pool.add_relay(relay).await?;
    79→        }
    80→
    81→        // Connect to all relays
    82→        self.pool.connect_all().await?;
    83→
    84→        *self.connected.write().await = true;
    85→        Ok(())
    86→    }
    87→
    88→    /// Disconnect from all relays
    89→    pub async fn disconnect(&self) -> Result<(), RelayError> {
    90→        self.pool.disconnect_all().await?;
    91→        *self.connected.write().await = false;
    92→        Ok(())
    93→    }
    94→
    95→    /// Check if connected to any relays
    96→    pub async fn is_connected(&self) -> bool {
    97→        *self.connected.read().await
    98→    }
