   100→    /// Subscribe to handler info events (NIP-89 kind 31990)
   101→    ///
   102→    /// This retrieves skill/agent/compute provider advertisements
   103→    pub async fn subscribe_handlers(
   104→        &self,
   105→        subscription_id: &str,
   106→        handler_type: Option<&str>,
   107→    ) -> Result<tokio::sync::mpsc::UnboundedReceiver<Event>, RelayError> {
   108→        if !self.is_connected().await {
   109→            return Err(RelayError::NoRelays);
   110→        }
   111→
   112→        // Build filter for NIP-89 handler info events
   113→        let mut filter = serde_json::json!({
   114→            "kinds": [KIND_HANDLER_INFO],
   115→            "limit": 100,
   116→        });
   117→
   118→        // Add handler type filter if specified
   119→        if let Some(htype) = handler_type {
   120→            filter["#handler"] = serde_json::json!([htype]);
   121→        }
   122→
   123→        let filters = vec![filter];
   124→        let rx = self.pool.subscribe(subscription_id, &filters).await?;
   125→
   126→        Ok(rx)
   127→    }
   128→
   129→    /// Subscribe to skill handler events
   130→    pub async fn subscribe_skills(
   131→        &self,
   132→        subscription_id: &str,
   133→    ) -> Result<tokio::sync::mpsc::UnboundedReceiver<Event>, RelayError> {
   134→        self.subscribe_handlers(subscription_id, Some("skill")).await
   135→    }
   136→
   137→    /// Subscribe to compute provider events
   138→    pub async fn subscribe_compute_providers(
   139→        &self,
   140→        subscription_id: &str,
   141→    ) -> Result<tokio::sync::mpsc::UnboundedReceiver<Event>, RelayError> {
   142→        self.subscribe_handlers(subscription_id, Some("compute_provider")).await
   143→    }
   144→
   145→    /// Subscribe to file metadata events (NIP-94 kind 1063)
   146→    pub async fn subscribe_file_metadata(
