   306→    }
   307→
   308→    /// Build advertisement event (kind 31990)
   309→    fn build_advertisement_event(
   310→        &self,
   311→        handler_info: &HandlerInfo,
   312→        secret_key: &str,
   313→    ) -> Result<Event, RelayError> {
   314→        use nostr::finalize_event;
   315→
   316→        let sk_bytes = self.parse_secret_key(secret_key)?;
   317→
   318→        // Set content from metadata
   319→        let content = serde_json::json!({
   320→            "name": handler_info.metadata.name,
   321→            "description": handler_info.metadata.description,
   322→            "icon_url": handler_info.metadata.icon_url,
   323→            "website": handler_info.metadata.website,
   324→        })
   325→        .to_string();
   326→
   327→        // Create event template for kind 31990 (handler info)
   328→        let template = EventTemplate {
   329→            kind: KIND_HANDLER_INFO,
   330→            tags: handler_info.to_tags(),
   331→            content,
   332→            created_at: SystemTime::now()
   333→                .duration_since(UNIX_EPOCH)
   334→                .unwrap_or_default()
   335→                .as_secs(),
   336→        };
   337→
   338→        // Sign and finalize event
   339→        let event = finalize_event(&template, &sk_bytes)
   340→            .map_err(|e| RelayError::Client(format!("Failed to sign event: {}", e)))?;
   341→
   342→        Ok(event)
   343→    }
   344→}
