    49→/// Metric dimension types for baseline tracking
    50→#[derive(Debug, Clone, Copy, PartialEq, Eq, Hash, Serialize, Deserialize)]
    51→pub enum MetricDimension {
    52→    /// Tool error rate (errors / total calls)
    53→    ToolErrorRate,
    54→    /// Task completion rate (completed / claimed)
    55→    CompletionRate,
    56→    /// Average duration per session (seconds)
    57→    AvgDuration,
    58→    /// Average cost per session (USD)
    59→    AvgCost,
    60→    /// Average tokens per session
    61→    AvgTokens,
    62→    /// Cache hit rate (cached / total input)
    63→    CacheHitRate,
    64→}
    65→
    66→impl MetricDimension {
    67→    pub fn as_str(&self) -> &'static str {
    68→        match self {
    69→            MetricDimension::ToolErrorRate => "tool_error_rate",
    70→            MetricDimension::CompletionRate => "completion_rate",
    71→            MetricDimension::AvgDuration => "avg_duration",
    72→            MetricDimension::AvgCost => "avg_cost",
    73→            MetricDimension::AvgTokens => "avg_tokens",
    74→            MetricDimension::CacheHitRate => "cache_hit_rate",
    75→        }
    76→    }
    77→
    78→    pub fn all() -> Vec<MetricDimension> {
    79→        vec![
    80→            MetricDimension::ToolErrorRate,
    81→            MetricDimension::CompletionRate,
    82→            MetricDimension::AvgDuration,
    83→            MetricDimension::AvgCost,
    84→            MetricDimension::AvgTokens,
    85→            MetricDimension::CacheHitRate,
    86→        ]
    87→    }
    88→}
