    90→/// Baseline calculator
    91→pub struct BaselineCalculator<'a> {
    92→    db: &'a MetricsDb,
    93→}
    94→
    95→impl<'a> BaselineCalculator<'a> {
    96→    pub fn new(db: &'a MetricsDb) -> Self {
    97→        Self { db }
    98→    }
    99→
   100→    /// Calculate baselines for all dimensions from recent sessions
   101→    pub fn calculate_baselines(&self, sessions: &[SessionMetrics]) -> Result<HashMap<String, Baseline>> {
   102→        let mut baselines = HashMap::new();
   103→
   104→        for dimension in MetricDimension::all() {
   105→            let values = self.extract_values(dimension, sessions);
   106→            if values.is_empty() {
   107→                continue;
   108→            }
   109→
   110→            let baseline = self.compute_baseline(dimension.as_str(), &values)?;
   111→            baselines.insert(dimension.as_str().to_string(), baseline);
   112→        }
   113→
   114→        Ok(baselines)
   115→    }
   116→
   117→    /// Extract metric values for a specific dimension
   118→    fn extract_values(&self, dimension: MetricDimension, sessions: &[SessionMetrics]) -> Vec<f64> {
   119→        sessions
   120→            .iter()
   121→            .filter_map(|s| match dimension {
   122→                MetricDimension::ToolErrorRate => {
   123→                    if s.tool_calls > 0 {
   124→                        Some(s.tool_errors as f64 / s.tool_calls as f64)
   125→                    } else {
   126→                        None
   127→                    }
   128→                }
   129→                MetricDimension::CompletionRate => {
   130→                    if s.issues_claimed > 0 {
   131→                        Some(s.issues_completed as f64 / s.issues_claimed as f64)
   132→                    } else {
   133→                        None
   134→                    }
   135→                }
   136→                MetricDimension::AvgDuration => Some(s.duration_seconds),
   137→                MetricDimension::AvgCost => Some(s.cost_usd),
   138→                MetricDimension::AvgTokens => Some((s.tokens_in + s.tokens_out) as f64),
   139→                MetricDimension::CacheHitRate => {
   140→                    let total_input = s.tokens_in + s.tokens_cached;
   141→                    if total_input > 0 {
   142→                        Some(s.tokens_cached as f64 / total_input as f64)
   143→                    } else {
   144→                        None
   145→                    }
   146→                }
   147→            })
   148→            .collect()
   149→    }
   150→
   151→    /// Compute baseline statistics from values
   152→    fn compute_baseline(&self, dimension: &str, values: &[f64]) -> Result<Baseline> {
   153→        if values.is_empty() {
   154→            anyhow::bail!("No values to compute baseline");
   155→        }
   156→
   157→        let mut sorted = values.to_vec();
   158→        sorted.sort_by(|a, b| a.partial_cmp(b).unwrap());
   159→
   160→        let mean = values.iter().sum::<f64>() / values.len() as f64;
   161→
   162→        let variance = values
   163→            .iter()
   164→            .map(|v| (v - mean).powi(2))
   165→            .sum::<f64>()
   166→            / values.len() as f64;
   167→        let stddev = variance.sqrt();
   168→
   169→        let p50 = percentile(&sorted, 0.50);
   170→        let p90 = percentile(&sorted, 0.90);
   171→        let p99 = percentile(&sorted, 0.99);
   172→
   173→        Ok(Baseline {
   174→            dimension: dimension.to_string(),
   175→            mean,
   176→            stddev,
   177→            p50,
   178→            p90,
   179→            p99,
   180→            sample_count: values.len() as i32,
   181→            updated_at: Utc::now(),
   182→        })
   183→    }
   184→
   185→    /// Update all baselines in the database
   186→    pub fn update_all_baselines(&self) -> Result<usize> {
   187→        // Get last 100 sessions for baseline calculation
   188→        let sessions = self.db.get_recent_sessions(100)?;
   189→
   190→        if sessions.is_empty() {
   191→            return Ok(0);
   192→        }
   193→
   194→        let baselines = self.calculate_baselines(&sessions)?;
   195→
   196→        for baseline in baselines.values() {
   197→            self.db.store_baseline(baseline)?;
   198→        }
   199→
   200→        Ok(baselines.len())
   201→    }
   202→}
