   204→/// Baseline comparator for regression detection
   205→pub struct BaselineComparator<'a> {
   206→    db: &'a MetricsDb,
   207→}
   208→
   209→impl<'a> BaselineComparator<'a> {
   210→    pub fn new(db: &'a MetricsDb) -> Self {
   211→        Self { db }
   212→    }
   213→
   214→    /// Compare current metrics against baselines and detect regressions
   215→    pub fn detect_regressions(&self, sessions: &[SessionMetrics]) -> Result<Vec<Regression>> {
   216→        let mut regressions = Vec::new();
   217→
   218→        let calculator = BaselineCalculator::new(self.db);
   219→        let current_baselines = calculator.calculate_baselines(sessions)?;
   220→
   221→        for dimension in MetricDimension::all() {
   222→            let dim_str = dimension.as_str();
   223→
   224→            // Get stored baseline
   225→            let stored_baseline = match self.db.get_baseline(dim_str)? {
   226→                Some(b) => b,
   227→                None => continue, // No baseline to compare against
   228→            };
   229→
   230→            // Get current baseline
   231→            let current_baseline = match current_baselines.get(dim_str) {
   232→                Some(b) => b,
   233→                None => continue,
   234→            };
   235→
   236→            // Check for regression (metric is worse than baseline)
   237→            let regression_detected = match dimension {
   238→                // Lower is better
   239→                MetricDimension::ToolErrorRate | MetricDimension::AvgDuration | MetricDimension::AvgCost => {
   240→                    current_baseline.mean > stored_baseline.mean * 1.10 // >10% worse
   241→                }
   242→                // Higher is better
   243→                MetricDimension::CompletionRate | MetricDimension::CacheHitRate => {
   244→                    current_baseline.mean < stored_baseline.mean * 0.90 // <10% of baseline
   245→                }
   246→                // Stable is better
   247→                MetricDimension::AvgTokens => {
   248→                    (current_baseline.mean - stored_baseline.mean).abs() > stored_baseline.mean * 0.20 // >20% change
   249→                }
   250→            };
   251→
   252→            if regression_detected {
   253→                let percent_change = ((current_baseline.mean - stored_baseline.mean) / stored_baseline.mean) * 100.0;
   254→
   255→                regressions.push(Regression {
   256→                    dimension: dimension,
   257→                    baseline_value: stored_baseline.mean,
   258→                    current_value: current_baseline.mean,
   259→                    percent_change,
   260→                    severity: if percent_change.abs() > 50.0 {
   261→                        RegressionSeverity::Critical
   262→                    } else if percent_change.abs() > 25.0 {
   263→                        RegressionSeverity::High
   264→                    } else {
   265→                        RegressionSeverity::Medium
   266→                    },
   267→                });
   268→            }
   269→        }
   270→
   271→        Ok(regressions)
   272→    }
   273→}
